CREATE PROCEDURE [dbo].[GetGlobalPayees] AS
/* This returns just the payees with SCOPE=GLOBAL. This query is very similar to the one used */
/* in FindPayees, but modified to count the number of payees aliased to each payee instead    */
/* of the number of members using the payee.                                                  */
/*                                                                                            */
/* To improve performance we get just the payees with GLOBAL scope before counting the number */
/* of Aliased Payees via the select into #p1 temporary table. */
select payeeid into #p1 from payee where scope='GLOBAL'
select e.payeeid,e.name, count(e2.PayeeAliasId) as NumAliased,
	e.address1,e.address2,e.address3, e.city, e.state, e.PostalCode,e.country,e.phone,
	e.LastUpdatedOn,e.LastUpdatedBy
from payee e  with (nolock) 
	inner join #p1 on e.payeeid=#p1.payeeid
	left join payee e2  with (nolock) on e.payeeid = e2.payeeAliasId
group by e.payeeid,e.name,e.address1,e.address2,
	e.address3, e.city, e.state, e.PostalCode,e.country,e.phone,
	e.LastUpdatedOn,e.LastUpdatedBy 
order by e.payeeid
if @@ERROR = 0 return(0) else return(-1)
GO
