CREATE PROCEDURE [dbo].[GetID]
	@id_type	char(32),
	@id		varchar(12) OUTPUT,
	@id_name	char(32) = ''	--Generic IDs only
AS

 SET NOCOUNT ON
IF @@LOCK_TIMEOUT = -1 SET LOCK_TIMEOUT 2500

  DECLARE @count 	int
  DECLARE @id_length 	int
  -- Verify a valid length type requested, determine length of id to return
  IF UPPER(@id_type) = 'PAYMENTID' OR UPPER(@id_type) = 'RECPAYMENTID'
	SELECT @id_length = 10
  ELSE IF UPPER(@id_type) = 'PAYEEID' OR UPPER(@id_type) = 'PAYEELISTID'
	SELECT @id_length = 12
  ELSE IF UPPER(@id_type) = 'RUNID' OR UPPER(@id_type) = 'GENERICID'
	SELECT @id_length = 0	 -- Returned as a numeric, not a 0-padded string
  ELSE
	GOTO invalid_type_error
  -- GenericIDs (only) require a id_name
  IF UPPER(@id_type) = 'GENERICID' AND @id_name = ''
	GOTO missing_id_name_error
  IF UPPER(@id_type) <> 'GENERICID' 
  BEGIN
   SET XACT_ABORT ON
     BEGIN TRANSACTION

     -- For non-GenericIDs, lock the row containing the requested IDType
/*     SELECT @count = COUNT(*) FROM Id (HOLDLOCK PAGLOCK) WHERE Type = @id_type
     -- If a row doesn't already exist for this type, it's an error. Table
     -- is populated at time it's created
     IF @count <> 1 
        GOTO missing_data_error
     ELSE IF @@error <> 0
	GOTO unknown_error
*/

     UPDATE ID SET Type = Type WHERE TYPE = @id_type
     IF @@ROWCOUNT = 0 
	GOTO missing_data_error
     ELSE IF @@ERROR <> 0 
	GOTO unknown_error
     SELECT @id = CurrentID FROM Id WITH (HOLDLOCK, TABLOCKX) WHERE Type = @id_type
	
     --If first time through, id will be ''; set to 0.
     --Check incrementing will exceed digits allowed for the IDType or
     --cuase an arithmetic overflow error; if so, recycle ids back to 0.
     --If won't excced digits or cause overflow error, increment id.  
     --Use decimal as intermediate type to avoid overflow errors -- the
     --range of ID values exceeds the range of the Int type.

     IF @id = '' 
	SELECT @id = '0'
     ELSE IF @id_length = 10 
	IF CONVERT(decimal(38,0), @id) = 9999999999.0 
	   SELECT @id = '0'
	ELSE
	   SELECT @id = LTRIM(STR(CONVERT(decimal(38,0), @id) + 1.0, @id_length, 0))
     ELSE IF @id_length = 12 
	IF CONVERT(decimal, @id) = 999999999999.0 
	   SELECT @id = '0'
	ELSE
	   SELECT @id = LTRIM(STR(CONVERT(decimal(38,0), @id) + 1.0, @id_length, 0))
     ELSE IF @id_length = 0
        IF CONVERT(int, @id) = 0x7fffffff 
	   SELECT @id = '0'
	ELSE
	   SELECT @id = LTRIM(STR(CONVERT(int, @id) + 1, 12, 0))
     --Pad id with zeros if ID needs to be 10 or 12 characters
     IF @id_length <> 0 
	SELECT @id = REPLICATE('0', @id_length - LEN(@id)) + @id
     --Update the table value with new current ID
     UPDATE ID Set CurrentID = @id WHERE Type = @id_type
     IF @@error <> 0
	GOTO unknown_error
     COMMIT TRANSACTION
   SET XACT_ABORT OFF
  END
  ELSE		-- Generic ID
  BEGIN
   SET XACT_ABORT ON
     BEGIN TRANSACTION
/*
     SELECT @count = COUNT(*) FROM Id (HOLDLOCK TABLOCKX) WHERE Type = @id_type AND Name = @id_name
     -- If a row doesn't already exist for this type, create it.
     IF @count = 0 
     -- Create a new GenericID with this name
     BEGIN
        SELECT @id = '0'
        INSERT ID (Type, CurrentID, Name) VALUES ('GenericID', @id, @id_name)
        IF @@error <> 0
	   GOTO unknown_error
     END
     ELSE IF @count = 1
     -- Increment existing GenericID with this name
*/
     BEGIN
        SELECT @id = CurrentID FROM Id WITH (HOLDLOCK, TABLOCKX) WHERE Type = @id_type AND Name = @id_name
	--Increment ID, or reset to 0 if incrementing would cause an overflow error
        IF CONVERT(int, @id) = 0x7fffffff 
	   SELECT @id = '0'
	ELSE
	   SELECT @id = LTRIM(STR(CONVERT(int, @id) + 1, 12, 0))
        UPDATE ID Set CurrentID = @id WHERE Type = @id_type AND Name = @id_name
        IF @@error <> 0
	   GOTO unknown_error
     END
/*
     ELSE IF @@error <> 0 OR @count > 1
	GOTO unknown_error
*/
     COMMIT TRANSACTION 
   SET XACT_ABORT OFF
     -- SELECT Id ONLY in the case of Generic IDs, all other ID Types are
     -- returned to the appropriate calling stored proc (PaymentCreate, 
     -- PayeeAdd, etc.) as an OUTPUT parameter.  RunIDs are also returned
     -- as OUTPUT parameters.  Returning also in SELECT statement causes 
     -- invocation from Marble FI framework to fail.
     SELECT 'ID'=@id
  END
  RETURN (0)
missing_data_error:
  ROLLBACK TRANSACTION
  RETURN (-2)
invalid_type_error:
  RETURN (-3)
missing_id_name_error:
  RETURN (-4)
unknown_error:
  ROLLBACK TRANSACTION
  RETURN (-1)
GO
