CREATE PROCEDURE [dbo].[GetPaymentStatus]
	@member_id	char(32),
	@payment_id	char(10)
AS
--
-- Check the PAYMENT table first.
--
DECLARE @code	int
DECLARE @date	smalldatetime
SELECT @code=StatusCode, @date=WillProcessDate FROM Payment  with (nolock) 
WHERE MemberID=@member_id And PaymentID=@payment_id
IF (@@ERROR <> 0) GOTO on_error
IF (@code IS NOT NULL) GOTO done
--
-- Didn't find it in PAYMENT, so check PAYMENT_HISTORY.
--
SELECT @code=StatusCode, @date=ProcessedDate FROM Payment_History  with (nolock) 
WHERE MemberID=@member_id And PaymentID=@payment_id
IF (@@ERROR <> 0) GOTO on_error
IF (@code IS NULL) GOTO not_found
done:
SELECT "Status"=0, "ProcCode"=@code,
"ProcDate"=RTRIM(CONVERT(char(15),@date,101)) + ' ' + RTRIM(CONVERT(char(15),@date,108))
RETURN (0)
not_found:
SELECT "Status"=1
RETURN (1)
on_error:
SELECT "Status"=-1
RETURN (-1)
GO
