CREATE PROCEDURE [dbo].[InsertPayee]
	@ProviderId     char(12),
	@Name           char(40),
	@Address1       char(32),
	@Address2       char(32)="",
	@Address3       char(32)="",
	@City           char(32)="",
	@State          char(5)="",
	@PostalCode     char(11)="",
	@Country        char(3)="",
	@Phone          char(32)="",
	@Scope          char(10),
	@BankId         char(9)="",
	@BranchId       char(22)="",
	@AccountId      char(22)="",
	@AccountType    char(1)="",
	@AccountKey     char(22)=""
/* This procedure is used to insert a new payee record into the PAYEE table. No checking is done */
/* to validate any of the data and it is assumed that the client program will check to ensure    */
/* that any field not allowing nulls has a value.                                                */
/*                                                                                               */
/* The LastUpdatedBy and LastUpdatedOn are entered here - as this is a convenient place to       */
/* to extract the user and domain information from the SPID of the user process.                 */
AS
declare @LastUpdatedOn datetime
declare @LastUpdatedBy char(32)
declare @Id	       char(12)
declare @rc	       int
select @LastUpdatedOn = getdate()
select @LastUpdatedBy = case 
				when nt_domain = null and nt_username=null then "unknown"
				when nt_domain = null and nt_username <> null then nt_username
				else rtrim(nt_domain) + "/" + nt_username 
			end
from master.dbo.sysprocesses 
	where spid=@@spid
EXEC @rc = GetID @id_type='PayeeID', @id=@ID OUTPUT	--KKS 17Sep1999 - ID Dispenser replacement
if @rc < 0 GOTO on_error
insert into Payee (PayeeId,PayeeAliasId,ProviderId,Name,Address1,Address2,Address3,City,State,
                   PostalCode,Country,Phone,Scope,BankId,BranchId,AccountId,AccountType,AccountKey,
				   LastUpdatedOn,LastUpdatedBy) 
values (
@Id,
"",
convert(int,@ProviderId),
@Name,
@Address1,
@Address2,
@Address3,
@City,
@State,
@PostalCode,
@Country,
@Phone,
@Scope,
@BankId,
@BranchId,
@AccountId,
@AccountType,
@AccountKey,
@LastUpdatedOn,
@LastUpdatedBy
)
if (@@ERROR <> 0) GOTO on_error
select 'Status'=1, 'PayeeID'=@id
return (1)
on_error:
select "Status"=-1
return (-1)
