/* This procedure is called to generate lists of payees based on simple parameters. */
as
declare @dtLo datetime, @dtHi datetime
if @iListType = 1         /* by Payee Id  */	
	select p1.*, p2.name as AliasName, pr.ProviderName from payee p1 with (nolock) 
		left join payee p2  with (nolock) on p1.PayeeAliasId = p2.payeeId
		inner join provider pr  with (nolock) on p1.ProviderId = pr.ProviderId
	where p1.PayeeId = @sSearch1
else if @iListType = 2         /* by list Id, memberid  */
	
	select p.*, u.CreatedOn, u.LastUpdatedOn as UserLastUpdatedOn, u.CreatedOn as UserCreatedOn from payee p with (nolock)  inner join 
		user_payee u  with (nolock) 
	on p.payeeid=u.payeeid 
	where u.PayeeListid=@sSearch1 and u.MemberId=@sSearch2
else if @iListType=3         /* by MemberId */
	
	select p.*, u.PayeeListId, u.CreatedOn, u.LastUpdatedOn as UserLastUpdatedOn, 
		u.CreatedOn as UserCreatedOn 
	from payee p  with (nolock) inner join user_payee u  with (nolock) on p.payeeid=u.payeeid 
	where u.MemberId=@sSearch1
	
else if @iListType = 4         /* by Alias Id  */	
	begin
	create table #p1 (payeeid char(12))
	insert into #p1 select payeeid from payee where PayeeAliasId = @sSearch1
	select p1.*, p2.name as AliasName, pr.ProviderName from payee p1
		inner join #p1 on p1.payeeid=#p1.payeeid
		left join payee p2 on p1.PayeeAliasId = p2.payeeId
		inner join provider pr on p1.ProviderId = pr.ProviderId
		order by p1.payeeid
	end
else if @iListType = 5         /* by date  */	
	begin
	
	select @dtLo = convert(datetime, @sSearch1),@dtHi = dateadd(day,1,convert(datetime,@sSearch1))
	select p1.*, pr.ProviderName from payee p1 with (nolock) 
		inner join provider pr  with (nolock) on p1.ProviderId = pr.ProviderId
		where p1.LastUpdatedOn between @dtLo and @dtHi
	end
else if @iListType = 6		/* by date range */
 
	begin
	
	select @dtLo = convert(datetime, @sSearch1), @dtHi = convert(datetime, @sSearch2)
	if datepart(hour, @dtHi)=0 and datepart(minute,@dtHi)=0 and datepart(second,@dtHi)=0 
		select @dtHi = dateadd(day,1,@dtHi)
	select p1.*, pr.ProviderName from payee p1  with (nolock) 
		inner join provider pr  with (nolock) on p1.ProviderId = pr.ProviderId
		where p1.LastUpdatedOn between @dtLo and @dtHi
	
	end
if @@error=0 return(0) else return(-1)
