else if @iListType = 2    /* date*/
	begin
	select @dtLo = convert(datetime, @sSearch1),@dtHi = dateadd(day,1,convert(datetime,@sSearch1))
	insert into #p1
	select PaymentId from payment_history where processeddate between
	   @dtLo and @dtHi
	end
else if @iListType = 3
	insert into #p1
	select PaymentId from payment_history where memberid=@sSearch1
else if @iListType = 4    /* date range*/
	begin
	select @dtLo = convert(datetime, @sSearch1), @dtHi = convert(datetime, @sSearch2)
	
	if datepart(hour, @dtHi)=0 and datepart(minute,@dtHi)=0 and datepart(second,@dtHi)=0 
		select @dtHi = dateadd(day,1,@dtHi)
	insert into #p1
	select paymentId from payment_history  with (nolock) where processeddate between @dtLo and @dtHi
	end
else if @iListType = 5 /* by memberid and date range */
	begin
	if @sSearch2 IS NULL and @sSearch3 IS NULL
		insert into #p1
		select paymentId from payment_history with (nolock)  where memberid=@sSearch1
	else if @sSearch2 IS NULL
		begin		
