		if datepart(hour, @dtHi)=0 and datepart(minute,@dtHi)=0 and datepart(second,@dtHi)=0 
			select @dtHi = dateadd(day,1,@dtHi)
		insert into #p1
		select paymentid from payment_history  with (nolock) where memberid=@sSearch1
			and ProcessedDate < @dtHi
		end
	else if @sSearch3 IS NULL
		begin		
		select @dtLo = convert(datetime, @sSearch2)
		insert into #p1
		select paymentid from payment_history  with (nolock) where memberid=@sSearch1
			and ProcessedDate > @dtLo
		end
