/****** Object:  Stored Procedure dbo.ListPmtHistAbridged    Script Date: 9/21/99 12:02:16 PM ******/
/*
  13April1999: KKS - Added to support new Bill Pay Admin Report 
  		     Lists all payments for a given MemberID, and/or in a given date range,
		     In addition, a PayeeID can optionally be specified.
  Note to self: there has got to be a cleaner way to do this.
 */
CREATE PROCEDURE [dbo].[ListPmtHistAbridged]
	@dtLo		datetime = '01/01/1900 00:00:00',
	@dtHi		datetime = '01/01/1900 00:00:00',
	@strMemberID	varchar(32) = '',
	@strPayee	varchar(64) = ''
AS
DECLARE	@strPayeeGlob varchar(65)
SELECT @strPayeeGlob = @strPayee + '%'
/* if @dtLo or @dtHi is blank, act as if default */
IF @dtLo = '' SELECT @dtLo = '01/01/1900 00:00:00'
IF @dtHi = '' SELECT @dtHi = '01/01/1900 00:00:00'
/* Validate data: at least a date range OR strMemberID must be set */
IF @dtLo = '01/01/1900 00:00:00' AND @dtHi = '01/01/1900 00:00:00' BEGIN
   /* Date not range set */
   /* If date range not set strMemberID must be */
   IF @strMemberID = '' GOTO on_error
   IF @strPayee = '' BEGIN
      /* strPayee is not part of criteria */
      SELECT MemberID      = RTRIM(ph.MemberID), 
      	     PaymentID     = RTRIM(ph.PaymentID), 
             PayeeName     = RTRIM(p.Name), 
             PayeeID       = RTRIM(ph.PayeeID),
             ProcessedDate = ph.ProcessedDate,
             StatusCode    = ph.StatusCode,
             PaymentMethod = RTRIM(pm.Description),
             Amount        = ph.Amount
      FROM Payment_History ph  with (nolock) 
	   INNER JOIN Payee p  with (nolock)  ON p.PayeeID = ph.PayeeID 
           INNER JOIN Payment_Method pm  with (nolock) ON ph.PaymentMethod = pm.PaymentMethod
      WHERE ph.MemberID = @strMemberID
      ORDER BY MemberID, ProcessedDate DESC
   END
   ELSE BEGIN
      /* strPayee is part of criteria */
      SELECT MemberID      = RTRIM(ph.MemberID), 
      	     PaymentID     = RTRIM(ph.PaymentID), 
             PayeeName     = RTRIM(p.Name),
             PayeeID       = RTRIM(ph.PayeeID),
             ProcessedDate = ph.ProcessedDate,
             StatusCode    = ph.StatusCode,
             PaymentMethod = RTRIM(pm.Description),
             Amount        = ph.Amount
      FROM Payment_History ph  with (nolock) 
	   INNER JOIN Payee p   with (nolock) ON p.PayeeID = ph.PayeeID 
           INNER JOIN Payment_Method pm  with (nolock) ON ph.PaymentMethod = pm.PaymentMethod
      WHERE ph.MemberID = @strMemberID AND p.Name LIKE @strPayeeGlob 
      ORDER BY MemberID, ProcessedDate DESC
   END 
END
ELSE BEGIN
   /* Date range is set */
   /* Invalid if only dtLo OR dtHi set */
   IF @dtLo = '01/01/1900 00:00:00' OR @dtHi = '01/01/1900 00:00:00' GOTO on_error
   IF @strMemberID = '' BEGIN
      /* strMemberID and strPayee are not part of criteria */
      SELECT MemberID      = RTRIM(ph.MemberID), 
      	     PaymentID     = RTRIM(ph.PaymentID), 
             PayeeName     = RTRIM(p.Name), 
             PayeeID       = RTRIM(ph.PayeeID),
             ProcessedDate = ph.ProcessedDate,
             StatusCode    = ph.StatusCode,
             PaymentMethod = RTRIM(pm.Description),
             Amount        = ph.Amount
      FROM Payment_History ph 
	   INNER JOIN Payee p   with (nolock) ON p.PayeeID = ph.PayeeID 
           INNER JOIN Payment_Method pm  with (nolock) ON ph.PaymentMethod = pm.PaymentMethod
      WHERE ph.ProcessedDate BETWEEN @dtLo AND @dtHi
      ORDER BY MemberID, ProcessedDate DESC
   END 
   ELSE BEGIN 
      IF @strPayee = '' BEGIN
         /* strPayee is not part of criteria */
         SELECT MemberID      = RTRIM(ph.MemberID), 
                PaymentID     = RTRIM(ph.PaymentID), 
                PayeeName     = RTRIM(p.Name), 
                PayeeID       = RTRIM(ph.PayeeID),
                ProcessedDate = ph.ProcessedDate,
                StatusCode    = ph.StatusCode,
                PaymentMethod = RTRIM(pm.Description),
                Amount        = ph.Amount
         FROM Payment_History ph with (nolock) 
            	INNER JOIN Payee 	  p  with (nolock)  ON p.PayeeID = ph.PayeeID 
         	INNER JOIN Payment_Method pm with (nolock)  ON ph.PaymentMethod = pm.PaymentMethod
         WHERE ph.MemberID = @strMemberID AND (ph.ProcessedDate BETWEEN @dtLo AND @dtHi)
      	 ORDER BY MemberID, ProcessedDate DESC
      END
      ELSE BEGIN
	    /* strPayeeID is part of criteria */
         SELECT MemberID      = RTRIM(ph.MemberID), 
                PaymentID     = RTRIM(ph.PaymentID), 
                PayeeName     = RTRIM(p.Name),
                PayeeID       = RTRIM(ph.PayeeID),
                ProcessedDate = ph.ProcessedDate,
                StatusCode    = ph.StatusCode,
                PaymentMethod = RTRIM(pm.Description),
                Amount        = ph.Amount
         FROM Payment_History ph with (nolock) 
            	INNER JOIN Payee 	  p   with (nolock) ON p.PayeeID = ph.PayeeID 
         	INNER JOIN Payment_Method pm with (nolock)  ON ph.PaymentMethod = pm.PaymentMethod
         WHERE ph.MemberID = @strMemberID AND p.Name LIKE @strPayeeGlob AND (ph.ProcessedDate BETWEEN @dtLo AND @dtHi)
      	 ORDER BY MemberID, ProcessedDate DESC
      END
   END
END 
IF (@@error <> 0) GOTO on_error
SELECT Status = "0"
RETURN (0)
on_error:
SELECT Status = "-1"
RETURN (-1)

GO
