	@new_name	char(32),
	@new_addr1	char(32),
	@new_addr2	char(32),
	@new_addr3	char(32),
	@new_city	char(32),
	@new_state	char(5),
	@new_postal	char(11),
	@new_country	char(3),
	@new_phone	char(32),
	@use_trans	char(1) = 'Y',	-- Optional: Y=use TRANSACTION/COMMIT, N=don't.
	@createdatein datetime = '01/01/1900 00:00:00'	-- Optional: 30May1999 KKS: OFX Certification
AS
--
-- This procedure assumes that the Payee table has already been scanned
-- and that the specified new payee is NOT listed.  We'll add a new Payee
-- record, and if necessary, a matching User_Payee record.
--
-- Modification : HLiou      1/8/2018  Always use USA as country designator.
--

IF (UPPER(@use_trans) = 'Y') BEGIN TRANSACTION
DECLARE @plid char(12)
DECLARE @days_to_pay 	int
DECLARE @createdate	datetime
DECLARE @payee_id	char(12)
DECLARE @rc		int
--SELECT @days_to_pay = mindaysforpmtbycheck from configuration
SELECT @days_to_pay = 0
-- KKS 30May1999: If an explicit createdate not given, 
-- then use current time; otherwise, use given one.
IF (@createdatein <> '01/01/1900 00:00:00') SELECT @createdate = @createdatein
ELSE SELECT @createdate = GetDate()
--
-- Step 1: Get the default Payment Provider ID
--
DECLARE @def_provider int
SELECT @def_provider = ProviderID FROM Provider WHERE DefaultProvider='Y'
