-- Step 2: Insert the new record.
--
EXEC @rc = GetID @id_type='PayeeID', @id=@payee_id OUTPUT
if @rc < 0 GOTO on_error
INSERT INTO Payee (Name, Address1, Address2, Address3, City, State, PostalCode,
	Country, Phone, Scope,  LastUpdatedOn, LastUpdatedBy,
	PayeeID, ProviderID, PayeeAliasID,
	BankID, BranchID, AccountID, AccountType, AccountKey )
