	'USA', @new_phone, 'USER',  @createdate, @member_id,
	@payee_id, @def_provider, '',
	'','','','','')
IF (@@ERROR <> 0) GOTO on_error
IF (UPPER(@use_trans) = 'Y') COMMIT TRANSACTION
SELECT 'Status'=0, 'PayeeID'=@payee_id
RETURN (0)
on_error:
PRINT 'Error Occurred'
IF (UPPER(@use_trans) = 'Y') ROLLBACK TRANSACTION
SELECT "Status"=-1
RETURN (-1)

