/*
 ==================================================================
  Stored Procedure Name: dbo.[PayeeDelete]

  Description: 

  Resources: 	User_Payee
               RECURRING_PAYMENT
               PAYMENT
               PAYMENT_HISTORY
               Payee
               User_Payee_Change_History
               Payee_Change_History
  ------------------------------------------------------------------
  Accepts:	   @member_id       char(32),
               @del_payeelistid char(12),
               @user_type		 tinyint,	-- Required parameter, the user type that caused the change.
               @change_reason	 char(255)	-- Required parameter, the reason the change occurred.

  Returns:		
   ==================================================================
   Created:  	?
   By:    		?
  ------------------------------------------------------------------
  Modifications: 
	06/15/2006  GNordstrom  Added this header block and added the output clause
	                        to the delete and update statements and removed the
	                        "exec admin_InsertChangeHistory".
=========================================================================
*/
CREATE PROCEDURE [dbo].[PayeeDelete]
	               @member_id       char(32),
	               @del_payeelistid char(12),
	               @user_type		 tinyint,	-- Required parameter, the user type that caused the change.
	               @change_reason	 char(255)	-- Required parameter, the reason the change occurred.
AS

DECLARE @payeeid char(12)
SELECT @payeeid = NULL

-- 
-- Step 1: Verify that the payee is already mapped to the member.  If not, quit.
-- 
SELECT @payeeid = PayeeID FROM User_Payee  with (nolock) 
WHERE MemberID=@member_id And PayeeListID=@del_payeelistid
IF (@@ERROR <> 0) GOTO on_select_error
IF (@payeeid is NULL) GOTO not_found

--
-- Step 2: Count the number of payments, recurring payments, and history this user_payee has.
--
DECLARE @num_rec_pmts	int
DECLARE @num_pmts		int
DECLARE @num_history	int

SELECT @num_rec_pmts	= COUNT(*) FROM RECURRING_PAYMENT	WHERE PayeeListID=@del_payeelistid

IF (@@ERROR <> 0) GOTO on_error

SELECT @num_pmts		= COUNT(*) FROM PAYMENT				WHERE PayeeListID=@del_payeelistid AND STATUSCODE = 100

IF (@@ERROR <> 0) GOTO on_error

SELECT @num_history		= COUNT(*) FROM PAYMENT_HISTORY		WHERE PayeeListID=@del_payeelistid

IF (@@ERROR <> 0) GOTO on_error
--
-- Step 2.5: Check if this is already 'deleted'
--
BEGIN TRANSACTION

DECLARE @activecount int
SELECT @activecount = COUNT(*) FROM User_Payee  with (nolock) WHERE MemberID=@member_id AND PayeeListID=@del_payeelistid AND Active = 'Y'
--
-- Step 3: If there are no recurring payments, payments, or history, then delete the user_payee.
--		   Otherwise, just mark it as not Active.
--

IF (@num_rec_pmts = 0 AND @num_pmts = 0 AND @num_history = 0)
BEGIN
	DELETE FROM User_Payee 
      OUTPUT DELETED.PayeeListID, DELETED.MemberID, DELETED.PayeeID, DELETED.CreatedOn, 
               DELETED.LastUpdatedOn, DELETED.UsersAccountAtPayee, DELETED.NameOnAccount, 
               DELETED.Active, DELETED.MinDaystoPay, DELETED.PaymentMethod, 
               @user_type, @change_reason, 'D', GETDATE(), ('HB-' + @member_id)  
         INTO User_Payee_Change_History (PayeeListID, MemberID, PayeeID, CreatedOn, 
               LastUpdatedOn, UsersAccountAtPayee, NameOnAccount, Active, MinDaystoPay, 
               PaymentMethod, User_Type, Reason, ModifyType, ModifyDate, ModifiedBy) 
	WHERE MemberID=@member_id And PayeeListID=@del_payeelistid
	IF (@@ERROR <> 0) GOTO on_error
END
ELSE
BEGIN
	Update User_Payee Set Active = 'N' 
      OUTPUT INSERTED.PayeeListID, INSERTED.MemberID, INSERTED.PayeeID, INSERTED.CreatedOn, 
               INSERTED.LastUpdatedOn, INSERTED.UsersAccountAtPayee, INSERTED.NameOnAccount, 
               DELETED.Active, INSERTED.MinDaystoPay, INSERTED.PaymentMethod, 
               @user_type, @change_reason, 'U', GETDATE(), ('HB-' + @member_id) 
         INTO User_Payee_Change_History (PayeeListID, MemberID, PayeeID, CreatedOn, 
               LastUpdatedOn, UsersAccountAtPayee, NameOnAccount, Active, MinDaystoPay, 
               PaymentMethod, User_Type, Reason, ModifyType, ModifyDate, ModifiedBy) 
	Where MemberID=@member_id And PayeeListID=@del_payeelistid
	IF (@@ERROR <> 0) GOTO on_error
END

--
-- Step 4: If no other members are using the PAYEE table record, delete it.
--
DECLARE @rows int
SELECT @rows = COUNT(*) FROM User_Payee with (nolock) WHERE PayeeID=@payeeid

IF (@@ERROR <> 0) GOTO on_error

IF (@rows <> 0) GOTO done

DELETE FROM Payee 
   OUTPUT DELETED.PayeeID, DELETED.PayeeAliasID, DELETED.ProviderID, DELETED.[Name], 
          DELETED.Address1, DELETED.Address2, DELETED.Address3, DELETED.City, 
          DELETED.State, DELETED.PostalCode, DELETED.Country, DELETED.Phone, 
          DELETED.Scope, DELETED.MinDaysToPay, DELETED.LastUpdatedOn, 
          DELETED.LastUpdatedBy, DELETED.BankID, DELETED.BranchID, DELETED.AccountID, 
          DELETED.AccountType, DELETED.PaymentMethod, DELETED.AccountKey, 
          DELETED.EntryDate, DELETED.PayeeType, DELETED.BillerKey, DELETED.AddressKey, 
          @user_type, @change_reason, 'D', GETDATE(), ('HB-' + @member_id)
      INTO Payee_Change_History 
         (PayeeID, PayeeAliasID, ProviderID, [Name], Address1, Address2, 
         Address3, City, State, PostalCode, Country, Phone, Scope, MinDaysToPay, 
         LastUpdatedOn, LastUpdatedBy, BankID, BranchID, AccountID, AccountType, 
         PaymentMethod, AccountKey, EntryDate, PayeeType, BillerKey, AddressKey,	
         User_Type, Reason, ModifyType, ModifyDate, ModifiedBy) 
WHERE PayeeID=@payeeid and Scope = 'USER'

IF (@@ERROR <> 0) GOTO on_error

done:
COMMIT TRANSACTION
-- KKS: added for Portal - OFX Certification want to know if already deleted.
if (@activecount = 0) GOTO not_found
SELECT "Status"=0
RETURN (0)


--
-- Error conditions
--
not_found:
   PRINT 'MemberID or PayeeListID is invalid'
   --ROLLBACK TRANSACTION
   SELECT "Status"=1
   RETURN (1)

on_error:
   PRINT 'Error Occurred'
   ROLLBACK TRANSACTION
   SELECT "Status"=-1
   RETURN (-1)

on_select_error:
   PRINT 'Error Occurred during select of payee information'
   SELECT "Status"=1
   RETURN 1



GO
