CREATE PROCEDURE [dbo].[PayeeSearch]
	@new_name    char(32),
	@new_addr1   char(32),
	@new_addr2   char(32),
	@new_addr3   char(32),
	@new_city    char(32),
	@new_state   char(5),
	@new_postal  char(11),
	@new_country char(3),
	@new_phone   char(32)  = ''	-- Optional
AS
-- 
-- Scan the PAYEE table to see the payee is already in the table. 
-- If so, return 0 and the PayeeListID, else return 1.
-- If a database error occurs, return -1.
-- 
DECLARE @payeeid char(12)
IF @new_phone = '' 
  SELECT @payeeid = PayeeID FROM Payee with (nolock) 
  WHERE 	UPPER(@new_name)    = UPPER(Name)
    And	UPPER(@new_addr1)   = UPPER(Address1)
    And	((Address2='' And @new_addr2='') Or (UPPER(@new_addr2) = UPPER(Address2)))   -- Optional field
    And	((Address3='' And @new_addr3='') Or (UPPER(@new_addr3) = UPPER(Address3)))   -- Optional field
    And	UPPER(@new_city)    = UPPER(City)
    And	UPPER(@new_state)   = UPPER(State)
    And	UPPER(@new_postal)  = UPPER(PostalCode)
    And	((Country='' And @new_country='') Or (UPPER(@new_country) = UPPER(Country)))   -- Optional field
ELSE
  SELECT @payeeid = PayeeID FROM Payee with (nolock) 
  WHERE 	UPPER(@new_name)    = UPPER(Name)
    And	UPPER(@new_addr1)   = UPPER(Address1)
    And	((Address2='' And @new_addr2='') Or (UPPER(@new_addr2) = UPPER(Address2)))   -- Optional field
    And	((Address3='' And @new_addr3='') Or (UPPER(@new_addr3) = UPPER(Address3)))   -- Optional field
    And	UPPER(@new_city)    = UPPER(City)
    And	UPPER(@new_state)   = UPPER(State)
    And	UPPER(@new_postal)  = UPPER(PostalCode)
    And	((Country='' And @new_country='') Or (UPPER(@new_country) = UPPER(Country)))   -- Optional field
    And	(@new_phone = Phone)   -- Optional field
IF (@@ERROR <> 0) GOTO on_error
IF (@payeeid IS NULL) GOTO no_match
SELECT "Status"=0, "PayeeID"=RTRIM(@payeeid)
RETURN (0)
no_match:
SELECT "Status"=1
RETURN (1)
on_error:
PRINT 'Error Occurred'
SELECT "Status"=-1
RETURN (-1)
GO
