/*
 ==================================================================
  Stored Procedure Name: PaymentCancel

  Description: 	

  Resources: 	Payment
               Payment_History
               User_Payee
               Payment_Change_History
  ------------------------------------------------------------------
  Accepts:		@member_id	char(32),
	            @payment_id	char(32),
	            @user_type	tinyint,	-- Required parameter, the user type that caused the change.
	            @change_reason	char(255),	-- Required parameter, the reason the change occurred.
	            @canceldate datetime = '01/01/1900 00:00:00'	-- optional, default to current KKS 13July1999

  Returns:		
   ==================================================================
   Created:  	?
   By:    		?
  ------------------------------------------------------------------
  Modifications: 
	06/15/06 GNordstrom  Added this header and added an ouput clause to 
	                     the delete statement and removed "exec AddChangeHistory".
	01/26/07 GNordstrom  Added insert of Payment.DueDate to DueDate field 
	                     in PaymentHistory table.  Also added insert of blank
	                     to MemberEmail field in PaymentHistory table.
	08/31/07 GNordstrom  Added insert of Payment.TrsfSource field to the
	                     same field in the Payment_Change_History table 
	                     and the PaymentHistory table.
	4/28/13  HLiou		 Added Delivery Date field
    12/10/13 HLiou		 Added Provider ID in payment history as provider sent for specific payment

==================================================================
*/
CREATE PROCEDURE [dbo].[PaymentCancel]
	      @member_id	char(32),
	      @payment_id	char(32),
	      @user_type	tinyint,	-- Required parameter, the user type that caused the change.
	      @change_reason	char(255),	-- Required parameter, the reason the change occurred.
	      @canceldate datetime = '01/01/1900 00:00:00'	-- optional, default to current KKS 13July1999
AS
BEGIN TRANSACTION
--
-- Step 1: Verify that the specified payment is in the PAYMENT table.
--	   If not, see if it's listed in the PAYMENT_HISTORY table.
--
DECLARE @rows int
DECLARE @payeelistid	char(12)
DECLARE @date_to_use	datetime
SELECT @rows = Count(*) FROM Payment 
WHERE MemberID=@member_id And PaymentID=@payment_id
IF (@@ERROR <> 0) GOTO on_error
IF (@rows = 1)
BEGIN
  SELECT @payeelistid=PayeeListID FROM Payment  with (nolock) 
  WHERE MemberID=@member_id And PaymentID=@payment_id
  IF (@@ERROR <> 0) GOTO on_error
  GOTO transfer_record
END
-- Not found in PAYMENT, so check PAYMENT_HISTORY
SELECT @rows = Count(*) FROM Payment_History  with (nolock) 
WHERE MemberID=@member_id And PaymentID=@payment_id
IF (@@ERROR <> 0) GOTO on_error
IF (@rows = 0) GOTO no_match
-- Found in PAYMENT_HISTORY, so get the status
DECLARE @status int
DECLARE @procdt smalldatetime, @faildt smalldatetime, @cancdt smalldatetime
SELECT @status=StatusCode, @procdt=ProcessedDate, @faildt=FailedDate, @cancdt=CancelledDate,
	@payeelistid=PayeeListID
FROM Payment_History  with (nolock) WHERE MemberID=@member_id And PaymentID=@payment_id
IF (@@ERROR <> 0) GOTO on_error
SELECT "Status"=@status, "StatusDate"=@procdt
COMMIT TRANSACTION
RETURN (0)
transfer_record:
--
-- Step 2: Copy the information from the PAYMENT record to a new PAYMENT_HISTORY record.
-- NOTES:  1: A StatusCode of '103' indicates a cancellation.
--	   2: The order of values in the INSERT statement must match the PAYMENT_HISTORY
--	      (not PAYMENT) table definition EXACTLY!!
--
-- First we need to get PayeeId and UsersAccountAtPayee, and NameOnAccount from USER_PAYEE table
DECLARE @payeeid char(12), @useracct char(32), @nameonacct char(60)
SELECT @payeeid = NULL

SELECT   @payeeid = PayeeID, 
         @useracct = UsersAccountAtPayee, 
         @nameonacct = NameOnAccount 
   FROM User_Payee  with (nolock) 
   WHERE MemberID=@member_id 
      And PayeeListID=@payeelistid
      
IF (@@ERROR <> 0) GOTO on_error
-- Use canceldate parameter if exists, otherwise default to current time
   IF (@canceldate <> '01/01/1900 00:00:00') BEGIN 
      SELECT @date_to_use = @canceldate
   END
   ELSE BEGIN
      SELECT @date_to_use = GetDate()
   END
   
INSERT INTO Payment_History 
   SELECT	PaymentID, PayeeListID, Account, Amount, '',
	         WillProcessDate, @date_to_use, NULL, @date_to_use, 
	         RecPaymentID, 103, Memo, BillReference, MemberID, 
	         @payeeid, 0, '', '', '', '', '', '', '', '', '', 
	         SourceApplication, @useracct, @nameonacct, '' ,'','', '', 
	         DueDate, TrsfSource, EntryDate, DeliveryDate,''
   FROM Payment 
   WHERE MemberID=@member_id 
      AND PaymentID=@payment_id

if (@@ERROR <> 0) GOTO on_error
--
-- Step 3: Delete the PAYMENT record.
--
DELETE FROM Payment 
	OUTPUT DELETED.PaymentID, DELETED.PayeeListID, DELETED.Account, DELETED.Amount, 
	      DELETED.WillProcessDate, DELETED.RecPaymentID, DELETED.StatusCode, DELETED.Memo, 
	      DELETED.BillReference, DELETED.LastUpdateDateTime, DELETED.MemberID, 
	      DELETED.SourceApplication, DELETED.EntryDate, DELETED.DueDate, @user_type, 
	      @change_reason, 'D', GETDATE(), ('HB-' + @member_id), DELETED.TrsfSource, DELETED.DeliveryDate 
	   INTO Payment_Change_History (PaymentID, PayeeListID, Account, Amount, WillProcessDate, 
	      RecPaymentID, StatusCode, Memo, BillReference, LastUpdateDateTime, MemberID, 
	      SourceApplication, EntryDate, DueDate, User_Type, Reason, ModifyType, ModifyDate, 
	      ModifiedBy, TrsfSource, DeliveryDate)  
WHERE MemberID=@member_id And PaymentID=@payment_id

if (@@ERROR <> 0) GOTO on_error

COMMIT TRANSACTION
SELECT "Status"=0
RETURN(0)
no_match:
ROLLBACK TRANSACTION
SELECT "Status"=9
RETURN(9)
on_error:
ROLLBACK TRANSACTION
SELECT "Status"=-1
RETURN(-1)




GO
