CREATE PROCEDURE [dbo].[PaymentCreate]
	@memberid	char(32),
	@payeelistid	char(12),
	@account	char(22),
	@amount		int,		-- in cents, not dollars!!
	@duedate	char(50),	-- date format used by SQL, e.g. 'Jan 01 1998'
	@memo		char(255),
	@billref	char(80),
	@srcappl	char(10),
	@payacct	char(32),
	@nameonacct char(32),
	@user_type	tinyint,
	@willprocessdate char(50),
	@createdatein datetime = '01/01/1900 00:00:00',	-- optional, default to current KKS 06April1999
	@TrsfSource varchar(22) = NULL,
	@DeliveryDate datetime = NULL 
AS
--
-- Local variables
--
DECLARE @pmts		int
DECLARE @pmtid		char(32)
DECLARE @payee_lead	int
DECLARE @createdate	datetime	
DECLARE @paymentid	char(10)
DECLARE @rc		int

--
-- Create a new Payment record.  Assume that the payee and account information
-- has already been validated.
--
IF (@createdatein <> '01/01/1900 00:00:00') BEGIN 
	SELECT @createdate = @createdatein
END
ELSE BEGIN
	SELECT @createdate = GetDate()
END
EXEC @rc = GetID @id_type='PaymentID', @id=@paymentid OUTPUT	--KKS 17Sep1999 - ID Dispenser replacement
if @rc < 0 GOTO on_error
INSERT INTO Payment
	(PayeeListID, MemberID, PaymentID, Account, Amount, WillProcessDate, DueDate, 
	 RecPaymentID, StatusCode, Memo, BillReference, SourceApplication, LastUpdateDateTime, TrsfSource,
	 DeliveryDate)
