/*
 ==================================================================
  Procedure Name: PaymentListSync
  
  Description:    Lists all pending and payment history for a member

  Resources: 	  
------------------------------------------------------------------
  Accepts:		@memberid	char(32),
	            @TokenDate	Datetime 

  Returns:		
   ==================================================================
   Created:  	?
   By:    		?

   Modification:

		04/28/2013	HLiou		Add DeliveryDate field
   ==================================================================
*/
CREATE PROCEDURE [dbo].[PaymentListSync] 
@MemberId char(32),
@TokenDate datetime
AS
SELECT RowType='P', PaymentID=RTRIM(p.PaymentID), PayeeListID=RTRIM(p.PayeeListID),
	Account=RTRIM(p.Account), Amount=p.Amount, StatusCode=(p.StatusCode), 
	Memo=RTRIM(p.Memo), BillRefInfo=RTRIM(p.BillReference),
	PayAcct=RTRIM(u.UsersAccountAtPayee), NameOnAcct=RTRIM(u.NameOnAccount), PayeeID=RTRIM(u.PayeeID),
	RecPaymentID=RTRIM(p.RecPaymentID),ProcessedDate=RTRIM(p.WillProcessDate),
	Name=RTRIM(e.Name), Scope=RTRIM(e.Scope), MinDaysToPay=u.MinDaysToPay, PaymentMethod=m.Description,
	WillProcessDate=p.WillProcessDate, p.DeliveryDate
FROM Payment p  with (nolock) 
	inner join User_Payee u with (nolock)  on p.PayeeListID=u.PayeeListID and p.MemberId=u.MemberId
	inner join Payee e  with (nolock) on u.PayeeID=e.PayeeID
	inner join Payment_Method m  with (nolock) on m.PaymentMethod = u.PaymentMethod
WHERE p.MemberId = @MemberId and p.LastUpdateDateTime > @TokenDate
UNION
SELECT RowType='PH', PaymentID=RTRIM(ph.PaymentID), PayeeListID=RTRIM(ph.PayeeListID),
	Account=RTRIM(ph.Account), Amount=ph.Amount, StatusCode=(ph.StatusCode), 
	Memo=RTRIM(ph.Memo), BillRefInfo=RTRIM(ph.BillReference),
	PayAcct=RTRIM(ph.UsersAccountAtPayee), NameOnAcct=RTRIM(ph.NameOnAccount), PayeeID=RTRIM(u.PayeeID),
	RecPaymentID=RTRIM(ph.RecPaymentID), ph.ProcessedDate,
	Name=RTRIM(e.Name), Scope=RTRIM(e.Scope), MinDaysToPay=u.MinDaysToPay, PaymentMethod=m.Description,
	WillProcessDate=ph.WillProcessDate, ph.DeliveryDate
FROM Payment_history ph with (nolock) 
	inner join User_Payee u  with (nolock) on ph.PayeeListID=u.PayeeListID and ph.MemberId=u.MemberId
	inner join Payee e  with (nolock) on u.PayeeID=e.PayeeID
	inner join Payment_Method m  with (nolock) on m.PaymentMethod = u.PaymentMethod
WHERE ph.MemberId = @MemberId and ph.ProcessedDate > @TokenDate 
ORDER BY ProcessedDate
/* 16April1999 Changed from ORDER BY PaymentId**/
if (@@error <> 0) goto on_error
return(0)
on_error:
return(-1)


GO
