/*
 ==================================================================
  Procedure Name: PaymentModify

  Description: 

  Resources: 	Payment
               Payment_Change_History
               User_Payee 
               Payment_History
  ------------------------------------------------------------------
  Accepts:		@member_id		   char(32),	-- Required parameter
	            @pmt_id			   char(10),	-- Required parameter
	            @payee_list_id	   char(12)  = NULL,
	            @account		      char(22)  = NULL,
	            @amount			   int       = NULL,
	            @memo			      char(255) = NULL,
	            @bill_ref		   char(80)  = NULL,
	            @due_date		smalldatetime = NULL,
	            @srcappl		      char(10) = NULL,
	            @payacct		      char(32) = NULL,
	            @nameonacct		   char(60) = NULL,
	            @user_type		   tinyint,	         -- Required parameter, the user type that caused the change.
	            @change_reason	   char(255),	      -- Required parameter, the reason the change occurred.
	            @willprocess_date	smalldatetime = NULL,
	            @TrsfSource          varchar(22) = NULL   

  Returns:		
   ==================================================================
   Created:  	?
   By:    		?
  ------------------------------------------------------------------
  Modifications: 
	06/15/2006  GNordstrom  Made the updates to the table one update statement and 
	                        added the ouput clause to the update statement.  Removed 
	                        the transaction since there is only one update statement 
	                        now and removed the "exec AddChangeHistory".
	01/24/2007  GNordstrom  Modified to update the DueDate field.
	08/31/2007  GNordstrom  Added new parameter @TrsfSource to update the TrsfSource field.
    4/28/2013   HLiou	   Added Delivery Date to payment history

==================================================================
*/
CREATE PROCEDURE [dbo].[PaymentModify]
	            @member_id		   char(32),	-- Required parameter
	            @pmt_id			   char(10),	-- Required parameter
	            @payee_list_id	   char(12)  = NULL,
	            @account		      char(22)  = NULL,
	            @amount			   int       = NULL,
	            @memo			      char(255) = NULL,
	            @bill_ref		   char(80)  = NULL,
	            @due_date		   smalldatetime = NULL,
	            @srcappl		      char(10) = NULL,
	            @payacct		      char(32) = NULL,
	            @nameonacct		   char(60) = NULL,
	            @user_type		   tinyint,	         -- Required parameter, the user type that caused the change.
	            @change_reason	   char(255),	      -- Required parameter, the reason the change occurred.
	            @willprocess_date	smalldatetime = NULL,
	            @TrsfSource       varchar(22) = NULL,
				@DeliveryDate		smalldatetime = NULL  
AS
--
-- This SP makes the following assumptions:
-- 1. The new account#, payment amount, and due date have already been validated.
-- 2. If a Payee modification was implied in the OFX request, it has already been
--    handled (i.e., the Payee identified by @payeelistid is correct).
--
--
-- First make sure the specified payment exists in the database.
--
DECLARE @rows int
SELECT @rows = Count(*) FROM Payment  with (nolock) WHERE MemberID=@member_id And PaymentID=@pmt_id
IF (@@ERROR <> 0) GOTO on_error
IF (@rows < 1) GOTO no_match
--
-- Update the UsersAccountAtPayee field
--
DECLARE @return_status int
exec @return_status=PayeeMappingModify @payee_list_id,@member_id,@payacct,@nameonacct,@user_type,@change_reason
if (@return_status <> 0) GOTO on_payee_error
--
-- Make the desired modification(s)
--

  UPDATE Payment 
  SET Account = @account, 
      Amount = @amount, 
      PayeeListID = @payee_list_id, 
      SourceApplication = @srcappl, 
      Memo = @memo, 
      BillReference = @bill_ref, 
      WillProcessDate = @willprocess_date, 
      DueDate  =  @due_date, 
      LastUpdateDateTime = GetDate(), 
      TrsfSource = @TrsfSource,
	  DeliveryDate = @DeliveryDate   
   OUTPUT INSERTED.PaymentID, DELETED.PayeeListID, DELETED.Account, DELETED.Amount, 
	      DELETED.WillProcessDate, INSERTED.RecPaymentID, INSERTED.StatusCode, 
	      DELETED.Memo, DELETED.BillReference, DELETED.LastUpdateDateTime, 
	      INSERTED.MemberID, DELETED.SourceApplication, INSERTED.EntryDate, 
	      DELETED.DueDate, @user_type, @change_reason, 'U', 
	      GETDATE(), ('HB-' + @member_id), DELETED.TrsfSource, DELETED.DeliveryDate
      INTO Payment_Change_History 
         (PaymentID, PayeeListID, Account, Amount, WillProcessDate, RecPaymentID, 
	      StatusCode, Memo, BillReference, LastUpdateDateTime, MemberID, 
	      SourceApplication, EntryDate, DueDate, User_Type, Reason, ModifyType, 
	      ModifyDate, ModifiedBy, TrsfSource, DeliveryDate)  
  WHERE MemberID=@member_id 
      AND PaymentID=@pmt_id
  
  IF (@@ERROR <> 0) GOTO on_error

SELECT "Status"=0
RETURN (0)
no_match:
-- We couldn't find the payment, but maybe it's already cancelled
SELECT @rows = Count(*) FROM Payment_History WHERE MemberID=@member_id And PaymentID=@pmt_id
IF (@@ERROR <> 0) GOTO on_error
IF (@rows < 1)
BEGIN
  -- Not found in payment history either, so the Pmt_id is bad
  SELECT "Status"=1
  RETURN (1)
END
-- Found in PAYMENT_HISTORY, so get the status
DECLARE @status int
DECLARE @procdt smalldatetime, @faildt smalldatetime, @cancdt smalldatetime
SELECT @status=StatusCode
FROM Payment_History WHERE MemberID=@member_id And PaymentID=@pmt_id
IF (@@ERROR <> 0) GOTO on_error
SELECT "Status"=@status
RETURN (1)
on_error:
ROLLBACK TRANSACTION
on_payee_error:
SELECT "Status"=-1
RETURN (-1)



GO
