CREATE PROCEDURE [dbo].[RecPmtCancel]
	            @recpmtid		char(10),
	            @user_type		tinyint,
	            @change_reason	char(255),
	            @canceldate datetime = '01/01/1900 00:00:00'	-- optional, default to current KKS 13July1999
AS
--
-- First make sure the specified recurring payment exists in the database.
--
  DECLARE @rows int
  DECLARE @member_id char(32)
  DECLARE @payeelistid char(12)
  DECLARE @date_to_use datetime
  DECLARE @willProcessDate	smalldatetime
  DECLARE @amount		int
--
-- Use canceldate parameter if exists, otherwise default to current time
--
   IF (@canceldate <> '01/01/1900 00:00:00') BEGIN 
      SELECT @date_to_use = @canceldate
   END
   ELSE BEGIN
      SELECT @date_to_use = GetDate()
   END
  
  SELECT @rows = Count(*) FROM Recurring_Payment  with (nolock) WHERE RecPaymentId = @recpmtid
  IF (@@ERROR <> 0) GOTO on_error
  IF (@rows < 1) GOTO no_match
  SELECT @member_id = MemberID, @payeelistid = PayeeListID, @WillProcessDate=NextProcessDate,  @amount =Amount
  FROM Recurring_Payment  with (nolock) WHERE RecPaymentId = @recpmtid
  
  UPDATE Recurring_Payment 
   SET Active = 'N' 
   OUTPUT DELETED.RecPaymentID, DELETED.PayeeListID, DELETED.MemberID, 
         DELETED.Account, DELETED.Active, DELETED.Amount, DELETED.InitialAmount, 
