CREATE PROCEDURE [dbo].[spCREATEFees]
@RUNID int AS

DECLARE @Return int
		
DECLARE @SQL varchar(255), 
		@delimiter char(1), 
		@SPName varchar(30),
		@ArchiveFile varchar(255),
		@ProviderID varchar(30), 
		@PCDirPath varchar(255), 
		@Message varchar(255), 
		@filefound varchar(50), 
		@ArchiveDirPath varchar(255), 
		@AdminAddress varchar(255),
		@PCFormat varchar(50), 
		@PCFileName varchar(255), 
		@ArchiveDir varchar(255), 
		@FiletoArchive varchar(255), 
		@newfilename varchar(255), 
		@FileName varchar(10)

DECLARE @now datetime

SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID))
SET @delimiter = '|'
SET @FileName = 'BPSym.out'
SET @Message = ' '
SET @Now = GETDATE()

SELECT @PCDirPath = RTRIM([OutBoundPaymentsFile]), @ArchiveDirPath = RTRIM([ArchiveDirectoryName]) FROM Provider WHERE ProviderID = 202
IF @@ROWCOUNT <> 1
	GOTO spError

SET @ArchiveDir = @PCDirPath + @ArchiveDirPath
SET @Message = 'Invalid Archive Directory defined in configuration table. ' + @ArchiveDir
IF dbo.udfPathExists( @ArchiveDir ) = 0
--IF @rc <> 0
BEGIN		-- If path does not exists, try to create it with the directory name in the configuration table
--	EXEC @rc = spCreateDirectory @ArchiveDir
	IF dbo.udfCreateDirectory (@ArchiveDir) = 0 
		GOTO spError
END

SET @Message = 'Error Getting Directory information'
SET @SQL = 'dir /B "' + @PCDirPath + @FileName
CREATE TABLE #PRFiles(C1 varchar(256)) INSERT #PRFiles EXEC master..xp_cmdshell @Command_String = @SQL
DELETE #PRFiles WHERE (C1 IS NULL)
IF @@ERROR <> 0
	GOTO spError


DECLARE cPRFiles CURSOR FOR SELECT C1 FROM #PRFiles
OPEN cPRFiles

FETCH NEXT FROM cPRFiles INTO @PCFileName
WHILE @@FETCH_STATUS = 0
BEGIN

	SET @FiletoArchive = @PCDirPath + @FileName
	IF @PCFileName = 'File Not Found'
		GOTO spContinue

	/*-- Make sure the file exists in case of File Not Found is returned from dir command
	EXEC @Return = sp_FileFound @FiletoArchive
	IF @Return <> 0    -- Then it does not exist, otherwise you get a zero
		GOTO spContinue*/

-- Look for new filename (renamed file) to archive
	IF RIGHT(@ArchiveDir, 1) <> '\'
		SET @ArchiveDir = @ArchiveDir  + '\'

	EXEC @Return = spGetFileDetailsLong @FiletoArchive ,@ArchiveFile OUTPUT 
	SET @ArchiveFile = RTRIM(@ArchiveDir)  + RTRIM(REPLACE(@ArchiveFile,' ','')) + @FileName+'_'+ CONVERT(varchar,@RunID-1)
	SET @Message = 'Could not move Payment Request File to archive directory: ' + @ArchiveFile
--	EXEC @Return = sp_FileMove @FiletoArchive, @ArchiveFile

	If dbo.udfMoveFile (@FiletoArchive, @ArchiveFile) = 0
	BEGIN
		SET @ArchiveFile = @ArchiveFile + '_' + REPLACE(REPLACE(REPLACE(Convert(varchar(16),@now,120),'-',''),':',''),' ','') 
		IF dbo.udfMoveFile ( @FiletoArchive, @ArchiveFile) = 0
			GOTO spContinue
	END
		
FETCH NEXT FROM cPRFiles INTO @PCFileName

END
spContinue:
	CLOSE cPRFiles
	DEALLOCATE cPRFiles

IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[#PRFILES]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE #PRFiles

DECLARE @tmptable table (paymentid varchar(10), MemberId varchar(20), Account varchar(20), Amount int,statuscode int, PayeeName varchar(40))

INSERT into @tmptable 
SELECT PaymentID = ph.PaymentID, 
	MemberId = ph.MemberID, 
	Account = RIGHT(ph.Account,2),
	Amount= ph.Amount,
	statuscode = ph.statuscode,
	PayeeName = p.name
FROM Payment_History ph WITH (NOLOCK) Inner Join Payee P WITH (NOLOCK) ON ph.payeeid = p.payeeid WHERE RUNID = @RunID and Statuscode = 200
UNION ALL
SELECT PaymentID= ph.PaymentID, 
	MemberId = ph.MemberID, 
	Account = RIGHT(ph.Account,2),
	Amount = ph.amount,
	statuscode = ph.statuscode, 
	PayeeName = p.name
FROM Payment_History ph WITH (NOLOCK) INNER JOIN Payee p WITH (NOLOCK) ON ph.payeeid = p.payeeid 
WHERE RUNID = @RunID and Statuscode in (101,102) 
ORDER BY memberid, 
		 account, 
		 amount DESC

IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[TEMP_REQUESTS]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE [dbo].[TEMP_REQUESTS]

CREATE TABLE [dbo].[TEMP_REQUESTS]
(
	[Request_ID] [int] IDENTITY (1, 1) NOT NULL ,
	[Request_Data] [varchar] (1000) NULL 
) ON [PRIMARY]

INSERT INTO  TEMP_REQUESTS
	SELECT
		Convert(Char(10),PaymentID) + @delimiter + 
		Convert(Char(6), MemberId) + @delimiter + 
		Convert(Char(2),RIGHT(Account,2)) + @delimiter +
		Convert(char(40),'NSF $' + 
		Convert(varchar,(Convert (decimal(10,2), amount/100.))) + 
		' BP to ' +  RTRIM(PayeeName))
FROM @tmptable where statuscode = 200

INSERT INTO  TEMP_REQUESTS
	SELECT
		Convert(Char(10),PaymentID) + @delimiter + 
		Convert(Char(6), MemberId) + @delimiter + 
		Convert(Char(2),RIGHT(Account,2)) + @delimiter + 
		Convert(char(40),'$'+ 
		Convert(varchar,(Convert (decimal(10,2), amount/100.))) + 
		' BP to ' +  RTRIM(PayeeName))
FROM @tmptable where statuscode != 200 

SET @Message = 'Error Encountered while Creating Outfile via bcp'
SET @SQL = 'bcp "SELECT Request_Data FROM ' + DB_Name() + '.dbo.TEMP_REQUESTS ORDER BY Request_ID" queryout "' + @FiletoArchive + '" -c -S' + @@SERVERNAME  + ' -T'
EXEC @Return = master..xp_cmdshell @SQL
IF @Return <> 0
	GOTO spError
SET @Message = ' '

spError:
EXEC splog @Message, @SPName
SET @Message = ''
IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[TEMP_REQUESTS]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE [dbo].[TEMP_REQUESTS]

