		@SettlementAddress varchar(255)

SET @SPName = 'spSendPaymentSummaryMessage'
SET @Now = GETDATE()
SET @To = ''

SET @TotalCount = @NumPayments + @FailedCount + @DupCount
SET @GrandTotal = @FailedAmount + @DupAmount + @TotalAmount
-- Get the email Subject and message text from the Email_Templates table and build the email to send out
SET @Message = 'Could not retrieve email details to send email to members/admin personnel'
SELECT @MsgText=ISNULL(MessageBody,' '),@Subject=ISNULL(MessageSubject,' '), @SendSysOp = EmailSysOp
	FROM NOTIFICATIONS WHERE ErrorNumber = @MessageType
IF @@ERROR <> 0
	GOTO spError

--Check to see if CC is enabled, if it is, then get the CC recipients address from the configuration table
SET @Message = ' Could not get CC information from Configuration Table'

SELECT @SenderAddress = ISNULL(ConfigValue,'BPGenerator') FROM Configuration WHERE ConfigName = 'EmailProfile'
SELECT @SettlementAddress= ISNULL(ConfigValue,' ') FROM Configuration WHERE ConfigName = 'EmailReportsAddress'
SET @To = @To + @SettlementAddress + ';'

IF @SendSysOp = 1
BEGIN
	SELECT @AdminAddress= ISNULL(ConfigValue,' ') FROM Configuration WHERE ConfigName = 'EmailSysOpAddress'
	SET @To = @To + @AdminAddress + ';'
END
--SELECT @MemberSvcAddress = ISNULL(ConfigValue,' ') FROM Configuration WHERE ConfigName = 'EmailMemberServicesAddress'

SELECT @CCAddress = ISNULL(ConfigValue,' ') FROM Configuration WHERE ConfigName = 'EmailCCAddress'
SET @CCenabled = 'Y'

IF @@ERROR <> 0 or @CCEnabled = 'N'
	SET @CCAddress = Null

SET @Subject = REPLACE( @Subject, '<Date>', CONVERT(varchar(12),@Now))
--  Fill in the template with the information retrieved from the DB
SET @MsgText = REPLACE( @MsgText , '<Date>' , CONVERT(varchar(12),@Now))
SET @MsgText = REPLACE( @MsgText, '<TIME>', SUBSTRING(CONVERT(varchar(20),@Now),13,20))
SET @MsgText = REPLACE( @MsgText, '<NumPayments>',ISNULL(Convert(varchar,@NumPayments),'0'))
SET @MsgText = REPLACE( @MsgText, '<FailedCount>',ISNULL(Convert(varchar,@FailedCount),'0'))
SET @MsgText = REPLACE( @MsgText, '<FailedAmount>',ISNULL(Convert(varchar,Convert(money,@FailedAmount/100.) ,1),'0'))
SET @MsgText = REPLACE( @MsgText, '<TotalAmount>',ISNULL(Convert(varchar,Convert(money,@TotalAmount/100.) ,1),'0'))
SET @MsgText = REPLACE( @MsgText, '<DuplicateCount>',ISNULL(Convert(varchar,@DupCount),'0'))
SET @MsgText = REPLACE( @MsgText, '<DuplicateAmount>', ISNULL(Convert(varchar,Convert(money,@DupAmount/100.) ,1),'0'))
SET @MsgText = REPLACE( @MsgText, '<TotalCount>',ISNULL(Convert(varchar,@TotalCount),'0'))
SET @MsgText = REPLACE( @MsgText, '<GrandTotal>',ISNULL(Convert(varchar,Convert(money,@GrandTotal/100.) ,1),'0'))
