CREATE PROCEDURE [dbo].[wc_AllRecPmtListSyncOrdered]
@TokenDate datetime
AS
-- By IDS 4/18/2000 for status screen
CREATE TABLE #p1 (
        RecPaymentID         char(10) NOT NULL,
 MemberID      char(32) NOT NULL,
    Name       char(40) NOT NULL,
 PayAcct       char(32) NOT NULL,
        Amount               int NOT NULL,
        InitialAmount        int NOT NULL,
        FinalAmount          int NOT NULL,
        InitialDueDate       smalldatetime NOT NULL,
        NumPayments          smallint NOT NULL,
        Frequency            char(15) NOT NULL,
 MinDaysToPay      smallint NOT NULL,
 PaymentMethod      char(100) NOT NULL,
        LastProcessedDate    smalldatetime NULL,
 PayeeListID      char(12) NOT NULL,
        PayeeID              char(12) NOT NULL,
 NameOnAcct      char(60) NOT NULL,
        Scope                char(10) NOT NULL,
      ProviderID int NOT NULL
 )
CREATE TABLE #p2 (
        RecPaymentID         char(10) NOT NULL,
 NextDueDate      smalldatetime NOT NULL,
 PaymentsLeft      smallint NOT NULL
 )
INSERT INTO #p1
SELECT  p.RecPaymentID,
 p.MemberID,
 e.Name, 
 u.UsersAccountAtPayee, 
 p.Amount, 
 p.InitialAmount,
 p.FinalAmount, 
 p.InitialDueDate,
 p.NumPayments, 
 p.Frequency,
 e.MinDaysToPay, 
 m.Description,
 p.LastProcessedDate,
        u.PayeeListID,
        e.PayeeID,
        u.NameOnAccount,
 e.Scope,
 e.ProviderID
FROM recurring_payment p  WITH (NOLOCK)  INNER JOIN 
 (User_Payee u  WITH (NOLOCK) inner join Payee e  WITH (NOLOCK) ON u.PayeeID=e.PayeeID)
ON p.PayeeListID=u.PayeeListID
 INNER JOIN Payment_Method m  WITH (NOLOCK) ON m.PaymentMethod = e.PaymentMethod
WHERE p.LastUpdateDateTime >= @TokenDate AND p.Active = 'Y'
ORDER BY RecPaymentId
if (@@error <> 0) goto on_error
DECLARE @tempDate  smalldatetime
DECLARE @i   int
DECLARE @initialDueDate  smalldatetime
DECLARE @lastProcessedDate  smalldatetime
DECLARE @frequency  char(15)
DECLARE @numPayments  smallint
DECLARE @minDaysToPay  smallint
DECLARE @paymentsLeft  smallint
DECLARE @recPaymentID           char(10)
DECLARE temp_cursor SCROLL CURSOR 
FOR SELECT RecPaymentID, InitialDueDate, LastProcessedDate, MinDaysToPay, NumPayments, Frequency FROM #p1
OPEN temp_cursor
FETCH FIRST FROM temp_cursor INTO @recPaymentID, @initialDueDate, @lastProcessedDate, @minDaysToPay, @numPayments, @frequency
WHILE ( @@FETCH_STATUS = 0) 
BEGIN
 SELECT @i = 0
 SELECT @tempDate = @initialDueDate
 SELECT @lastProcessedDate = DATEADD(day, @minDaysToPay, @lastProcessedDate)
 -- We must loop through all the payment dates until we reach one that is after
 -- The last processed date taking into account the MinDaysToPay for this Payee.
 -- Therefore, we start by adding minDaysToPay to the lastProcessedDate.
 -- Add the frequency until we surpass the LastProcessedDate (or run out of payments).
 -- If we run out of payments, it is an error since the payment should be closed.
 --
 WHILE (@tempDate <= @lastProcessedDate) AND (@i <= @numPayments)
 BEGIN
  SELECT @tempDate =
  CASE @frequency
  WHEN 'WEEKLY'   THEN DATEADD(week,1,@tempDate)
  WHEN 'BIWEEKLY'  THEN DATEADD(week,2,@tempDate)
  WHEN 'FOURWEEKS'  THEN DATEADD(week,4,@tempDate)
  WHEN 'MONTHLY'   THEN DATEADD(month,1,@tempDate)
  WHEN 'BIMONTHLY'  THEN DATEADD(month,2,@tempDate)
  WHEN 'SEMIANNUALLY' THEN DATEADD(month,6,@tempDate)
  WHEN 'QUARTERLY'  THEN DATEADD(quarter,1,@tempDate)
  WHEN 'ANNUALLY'  THEN DATEADD(year,1,@tempDate)  
  END
  SELECT @i = @i + 1
 END
 --
 -- ...now tempDate is the next payment date.
 -- Figure out the payment amount.  If the next payment is the last payment, then
 -- the payment amount is finalAmount.  Otherwise it is just amount.
 SELECT @paymentsLeft = @numPayments - @i
 --UPDATE #p1 SET NextDueDate=@tempDate, PaymentsLeft=@paymentsLeft WHERE CURRENT OF temp_cursor
 INSERT INTO #p2 VALUES (@recPaymentID, @tempdate, @paymentsLeft)
 FETCH NEXT FROM temp_cursor INTO @recPaymentID, @initialDueDate, @lastProcessedDate, @minDaysToPay, @numPayments, @frequency
END
CLOSE temp_cursor
DEALLOCATE temp_cursor
SELECT  RecPaymentID = p1.RecPaymentID,
        MemberID = RTRIM(p1.MemberID),
 Name = RTRIM(p1.Name),
 PayAcct = RTRIM(p1.PayAcct),
        Amount = p1.Amount,
        FinalAmount = p1.FinalAmount,
        InitialDueDate = p1.InitialDueDate,
        NumPayments = p1.NumPayments,
        Frequency = RTRIM(p1.Frequency),
 MinDaysToPay = p1.MinDaysToPay,
 PaymentMethod = RTRIM(p1.PaymentMethod),
        LastProcessedDate = p1.LastProcessedDate,
 PayeeListID = p1.PayeeListID,
        PayeeID = p1.PayeeID,
 NameOnAcct = p1.NameOnAcct,
        Scope = p1.Scope,
 NextDueDate = p2.NextDueDate, 
 PaymentsLeft = p2.PaymentsLeft,
 ProviderID = p1.ProviderID
FROM #p1 p1 INNER JOIN #p2 p2 ON p1.RecPaymentID = p2.RecPaymentID 
ORDER BY NextDueDate 
return(0)
on_error:
return(-1)
GO
