Declare @WinningAccount int
Declare @RandomTime datetime
Declare @RandomNumber tinyint
Declare @WinningCount int
Select Date from BillPay_A_Day  where (WinningAccount=@MemberID) and (datepart(m,Date) = datepart(m,getdate()))
IF @@ROWCOUNT > 0
BEGIN
 print 'You were already a winner this month'
 Return (4)
END
 
Declare c CURSOR FOR
Select WinningAccount,RandomTime,RandomNumber from BillPay_A_Day
WHERE DateDiff(d,Date,getdate()) = 0
FOR UPDATE
OPEN c
Fetch NEXT FROM c INTO @WinningAccount,@RandomTime,@RandomNumber
IF @@FETCH_STATUS <> 0 
BEGIN
 print 'no record for today'' date'
 Select @RetValue = 0
END
ELSE
BEGIN
 print 'Today is a playing day, checking to see if the member is a winner'
 IF @RandomNumber < 2 or @RandomNumber > 14
 BEGIN
  print 'today is not a current-activity type day'
  Select @RetValue = 1
 END
 ELSE
 BEGIN
  IF @WinningAccount is not  null
  BEGIN
   print 'we already have a winner today'
   Select @RetValue = 2
  END
  ELSE
  BEGIN
   IF @RandomTime > getdate()
   BEGIN
    print 'Too early to be a winner for today'
    Select @RetValue = 3
   END
   ELSE
   BEGIN
    print 'You are today''s winner!'
    Update BillPay_A_Day set
      WinningAccount = @MemberID,
     Amount = @Amount,
     PaymentID = @PaymentID,
     RecPaymentID = @RecPAymentID,
      WinningDate = getDate(),
     DueDate = @DueDate,
     WinName = @WinName
    where current of c
    Select @RetValue = 100
   END
  END
 END
END
CLOSE c
DEALLOCATE c
return (@RetValue)
