CREATE PROCEDURE [dbo].[wc_BP_ActiveUsers2]
                     @S smalldatetime,		-- Start Date
                     @E smalldatetime,		-- End Date
                     @Count int OUTPUT
AS
--Declare @E smalldatetime		-- End Date
--Declare @S smalldatetime		-- Start Date
--Select @E = getdate()
--Select @S = dateadd(d,-30, @E)
--print @E
--print @S
select @Count = count  (distinct memberid) from (
SELECT DISTINCT(MemberID) 
   FROM User_Payee_Change_History 
   WHERE ModifyDate >= @S 
      AND ModifyDate <= @E       
union
SELECT DISTINCT(MemberID)
   FROM Payment_Change_History 
   WHERE ModifyDate >= @S 
      AND ModifyDate <= @E       
union
SELECT DISTINCT(MemberID)
   FROM Recurring_Payment_Change_History 
   WHERE ModifyDate >= @S 
      AND ModifyDate <= @E       
union
select distinct user_payee.memberid 
   from change_history join user_payee on change_history.payeelistID = user_payee.payeelistid 
	where change_history.lastupdatedon >= @S 
	   and change_history.lastupdatedon <= @E 
	   and change_history.payeelistid is not null 
union
select distinct payment.memberid 
   from change_history join payment on change_history.paymentid = payment.paymentid 
	where change_history.lastupdatedon >= @S 
	   and change_history.lastupdatedon <= @E 
	   and change_history.paymentid is not null 
union
select distinct H.memberid 
   from change_history join payment_History H on change_history.paymentid = H.paymentid 
	where change_history.lastupdatedon >= @S 
	   and change_history.lastupdatedon <= @E 
	   and change_history.paymentid is not null 
-- Following scan of History broken into three scans to increase speed -- helped greatly
--union
-- select distinct h.memberid from payment_history h 
--	where ( (h.processeddate >= @S and h.processeddate <= @E) or 
--	(h.cancelleddate >= @S  and h.cancelleddate <= @E ) or
--	(h.faileddate >= @S  and h.faileddate <= @E ) )
union
  select distinct h.memberid 
   from payment_history h 
	where ( (h.processeddate >= @S and h.processeddate <= @E)  )
union
  select distinct h.memberid 
   from payment_history h 
	where (	(h.cancelleddate >= @S  and h.cancelleddate <= @E )  )
union
  select distinct h.memberid 
   from payment_history h 
	where ( (h.faileddate >= @S  and h.faileddate <= @E ) )
union 
  select distinct p.memberid 
   from payment p 
	where p.lastupdatedatetime >= @S and p.lastupdatedatetime <= @E
union 
  select distinct r.memberid 
   from recurring_payment r 
	where r.lastupdatedatetime >= @S and r.lastupdatedatetime <= @E
) P
return @Count
