/*
 ==================================================================
  SP Name:     wc_BP_DailyActiveAccounts

  Description: Obtains the MemberID and latest update date and time 
               for all activity that has occurred on or since the last 
               bill pay processing day.

  Resources: 	change_history
               user_payee
               payment
               payment_History
               recurring_payment
               User_Payee_Change_History
               Payment_Change_History
               Recurring_Payment_Change_History
  ------------------------------------------------------------------
  Accepts:		none 

  Returns:		MemberID,
               LastDate
   ==================================================================
   Created:  	?
   By:    		?
  ------------------------------------------------------------------
  Modifications: 
      11/28/06 GNordstrom  Added this header and additional select statements
                           to obtain counts from the new change history tables.

==================================================================
*/
CREATE PROCEDURE [dbo].[wc_BP_DailyActiveAccounts]
AS

Declare @S smalldatetime		-- Start Date

--  On Sunday we need to go back to Friday to get all the updates
IF DATEPART(dw, GETDATE()) = 1
	Select @S = dateadd(d,-3, getdate())
ELSE
	Select @S = dateadd(d,-2, getdate())

select   memberid, 
         LastDate= max(LastDate) 
   from (
SELECT MemberID, MAX(ModifyDate) as LastDate
   FROM User_Payee_Change_History 
   WHERE ModifyDate >= @S 
   GROUP BY MemberID 
union
SELECT MemberID, MAX(ModifyDate) as LastDate
   FROM Payment_Change_History 
   WHERE ModifyDate >= @S 
   GROUP BY MemberID 
union
SELECT MemberID, MAX(ModifyDate) as LastDate
   FROM Recurring_Payment_Change_History 
   WHERE ModifyDate >= @S 
   GROUP BY MemberID 
union
  select user_payee.memberid,LastDate=max(change_history.lastupdatedon)  
   from change_history  with (nolock) join user_payee  with (nolock) on change_history.payeelistID = user_payee.payeelistid 
	where change_history.lastupdatedon >= @S  
	   and change_history.payeelistid is not null 
	group by user_payee.memberid
union
 select  payment.memberid,LastDate=max(change_history.lastupdatedon)  
   from change_history  with (nolock) join payment  with (nolock) on change_history.paymentid = payment.paymentid 
	where change_history.lastupdatedon >= @S  
	   and change_history.paymentid is not null 
	group by payment.memberid
union
 select  H.memberid,LastDate=max(change_history.lastupdatedon)  
   from change_history with (nolock)  join payment_History H  with (nolock) on change_history.paymentid = H.paymentid 
	where change_history.lastupdatedon >= @S  
	   and change_history.paymentid is not null 
	group by H.memberid
-- Following scan of History broken into three scans to increase speed -- helped greatly
--union
-- select distinct h.memberid from payment_history h 
--	where ( (h.processeddate >= @S and h.processeddate <= @E) or 
--	(h.cancelleddate >= @S  and h.cancelleddate <= @E ) or
--	(h.faileddate >= @S  and h.faileddate <= @E ) )
union
 select h.memberid,max(h.processeddate) 
   from payment_history h  with (nolock) 
	where h.processeddate >= @S  
	group by h.memberid
union
 select h.memberid,max(h.cancelleddate) 
   from payment_history h  with (nolock) 
	where h.cancelleddate >= @S   
	group by h.memberid
union
 select h.memberid,max(h.faileddate) 
   from payment_history h  with (nolock) 
	where  h.faileddate >= @S   
	group by h.memberid
union 
 select p.memberid,max(p.lastupdatedatetime) 
   from payment p  with (nolock) 
	where p.lastupdatedatetime >= @S 
	group by p.memberid
union 
 select r.memberid,max(r.lastupdatedatetime) 
   from recurring_payment r  with (nolock) 
	where r.lastupdatedatetime >= @S 
	group by r.memberid
) P group by memberid
order by memberid

GO
