CREATE PROCEDURE [dbo].[wc_GetPayeeLastPayment]
                  @MemberID Char(32)
AS

SET NOCOUNT ON;

--Get the last successful payment info for the member's active payees.
CREATE TABLE #paid (PayeeListID varchar(12), 
                    LastProcDate smalldatetime, 
                    PaymentID    varchar(10))
INSERT INTO #paid (PayeeListID, LastProcDate, PaymentID) 
 SELECT  u.PayeeListID as plid, 
            MAX(h.ProcessedDate) as ld, 
            (SELECT TOP 1 PaymentID FROM Payment_History WITH (NOLOCK) WHERE PayeeListID = u.PayeeListID 
            and ProcessedDate = MAX(h.ProcessedDate)) as PaymentID 
      FROM USER_PAYEE u inner join payment_history h on u.payeelistid = h.payeelistid
      where u.MemberID = @memberid and u.Active = 'Y' and h.StatusCode IN (101, 102) 
         GROUP BY u.PayeeListID;
         
--Get the member's active payees with the last processed date and amount for each of the payees.
WITH LastPayment AS
      (SELECT  u.PayeeListID, 
               MAX(h.ProcessedDate) AS LastProcDate, 
               (SELECT TOP 1 PaymentID FROM Payment_History WITH (NOLOCK) WHERE PayeeListID = u.PayeeListID and ProcessedDate = MAX(h.ProcessedDate)) as PaymentID                   
         FROM user_payee u Inner JOIN Payment_History h WITH (NOLOCK) ON u.PayeeListID = h.PayeeListID 
         WHERE u.PayeeListID NOT IN (SELECT PayeeListID FROM #paid)
            GROUP BY u.PayeeListID
      UNION 
