/*
 ==================================================================
  SP Name:     wc_GetPayeeListBox

  Description: 

  Resources: 	Payee
               User_Payee
               wc_NickNames
  ------------------------------------------------------------------
  Accepts:     @MemberID Char(32)
               
  Returns:    
   ==================================================================
   Created:  	?
   By:    	   ? 
  ------------------------------------------------------------------
  Modifications: 
   08/10/2007  GNordstrom  Added returning last successful payment date 
                           and amount for each payeelistid. Also added 
                           aliases for tables.
   08/22/2007  GNordstrom  Added return providerID.
   02/04/2008  GNordstrom  Added condition of memberId to the wc_NickNames join.
==================================================================
*/
CREATE PROCEDURE [dbo].[wc_GetPayeeListBox]
                  @MemberID Char(32)
AS

SET NOCOUNT ON;

--Get the member's active payees from the User_Payee table. 
CREATE TABLE #up (PayeeListID varchar(12), 
                  PayeeID varchar(12), 
                  UsersAccountAtPayee varchar(32), 
                  PaymentMethod varchar(1))
INSERT INTO #up (PayeeListID, PayeeID, UsersAccountAtPayee, PaymentMethod)       
   SELECT   PayeeListID, 
            PayeeID, 
            RTRIM(UsersAccountAtPayee),
            PaymentMethod 
      FROM User_Payee WITH (NOLOCK)  
      WHERE MemberID = @MemberID 
         AND Active = 'Y'

--Get the last successful payment info for the member's active payees.
CREATE TABLE #paid (PayeeListID varchar(12), 
                    LastProcDate smalldatetime, 
                    PaymentID    varchar(10))
INSERT INTO #paid (PayeeListID, LastProcDate, PaymentID) 
    SELECT  u.PayeeListID as plid, 
            MAX(h.ProcessedDate) as ld, 
            (SELECT TOP 1 PaymentID FROM Payment_History WITH (NOLOCK) WHERE PayeeListID = u.PayeeListID and ProcessedDate = MAX(h.ProcessedDate)) as PaymentID                  
      FROM #up u LEFT JOIN Payment_History h WITH (NOLOCK) ON u.PayeeListID = h.PayeeListID 
      WHERE h.StatusCode IN (101, 102) 
         GROUP BY u.PayeeListID;

--Get the member's active payees with the last processed date and amount for each of the payees.
WITH LastPayment AS
      (SELECT  u.PayeeListID, 
               MAX(h.ProcessedDate) AS LastProcDate, 
               (SELECT TOP 1 PaymentID FROM Payment_History WITH (NOLOCK) WHERE PayeeListID = u.PayeeListID and ProcessedDate = MAX(h.ProcessedDate)) as PaymentID,
               'N' AS ValidPymt                   
         FROM #up u LEFT JOIN Payment_History h WITH (NOLOCK) ON u.PayeeListID = h.PayeeListID 
         WHERE u.PayeeListID NOT IN (SELECT PayeeListID FROM #paid)
            GROUP BY u.PayeeListID
      UNION 
      SELECT PayeeListID, 
             LastProcDate, 
             PaymentID, 
             'Y' AS ValidPymt 
         FROM #paid  
      )
SELECT   u.PayeeListID, 
         COALESCE(n.NickName, p.Name) AS Name,
         u.UsersAccountAtPayee AS UsersAccounAtPayee,
         u.PaymentMethod, 
         ISNULL(CONVERT(varchar, lp.LastProcDate, 101), 'n/a') AS LastProcDate, 
	      ISNULL(('$' + CONVERT(varchar, CONVERT(money, h.Amount/100.0), 1)), 'n/a') AS LastAmount, 
         lp.ValidPymt, 
         p.ProviderID   
FROM #up u INNER JOIN PAYEE p WITH (NOLOCK) ON u.PayeeID = p.PayeeID 
      INNER JOIN LastPayment lp ON u.PayeeListID = lp.PayeeListID 
      LEFT JOIN wc_NickNames n WITH (NOLOCK) ON u.PayeeListID = n.PayeeListID AND n.MemberID = @MemberID 
      LEFT JOIN Payment_History h WITH (NOLOCK) ON lp.PaymentID = h.PaymentID   
   ORDER BY Name   

DROP TABLE #up
DROP TABLE #paid 

SET NOCOUNT OFF


GO
