  ------------------------------------------------------------------
  Modifications: 
	01/02/07 GNordstrom  Added output clause to the update  and 
	                     delete wc_NickNames statements.  Added 
	                     this header.
==================================================================
*/
CREATE PROCEDURE [dbo].[wc_InsertNickName]
      @PayeeListID char(12),
      @MemberID varchar(22),
      @NickName varchar(50)
AS
-- (A trigger handles deleting records)
-- Note: there should never be a case where the MemberID does not match for a given 
-- PayeeListID, we are including the MemberID in the select for safety.
-- In the event the MemberID is incorrect, and the select fails, the INSERT
-- will fail because the PayeeListID is a Primary Key field.
Set @NickName = rtrim(ltrim(@NickName))

IF EXISTS (SELECT PayeeListID FROM WC_NickNames WHERE PayeeListID = @PayeeListID and MemberID = @MemberID)	
BEGIN
	IF len(@NickName) = 0
		DELETE FROM WC_NickNames 
		   OUTPUT DELETED.PayeeListID, DELETED.NickName, DELETED.MemberID, 
		      'D', GETDATE(), ('HB-' + @MemberID) 
		   INTO wc_NickNames_Change_History 
		WHERE PayeeListID = @PayeeListID
	ELSE
		UPDATE WC_NickNames  
		   SET NickName = @NickName 
		OUTPUT INSERTED.PayeeListID, DELETED.NickName, INSERTED.MemberID, 
		      'U', GETDATE(), ('HB-' + @MemberID) 
		   INTO wc_NickNames_Change_History 
		WHERE PayeeListID = @PayeeListID
END
ELSE
BEGIN
	IF len(@NickName) > 0
		INSERT INTO WC_NickNames (PayeeListID, MemberID, NickName) VALUES (@PayeeListID, @MemberID, @NickName)

END



