-- =============================================
-- Author:		Helen Liou	
-- Create date: 6/23/2013
-- Description:	This sp will return all the non-processing days for the calendar
-- =============================================
CREATE PROCEDURE [dbo].[wc_NonProcessingDays]
AS
BEGIN
	SET NOCOUNT ON;
	DECLARE @rc int, 
			@RunDays varchar(255),
			@ReportYears int,
			@Skip int
	

	DECLARE @tmpHolidayTable TABLE ( NonProcessingDay Datetime, NonProcessingDate varchar(10) )
	
	SET DATEFIRST 7
--  Get list of non-processing days, then keep adding the holidays too
	EXEC @rc = any_GetConfigurationValue 'RunDays',@RunDays OUTPUT
	EXEC @rc = any_GetConfigurationValue 'ReportLookBackYear', @ReportYears OUTPUT
	EXEC @rc = any_GetConfigurationValue 'SkipHoliday', @skip OUTPUT
	
	print @skip

	INSERT INTO @tmpHolidayTable (NonProcessingDay, NonProcessingDate) values (GETDATE(), ' ')
 
	WHILE (( SELECT MAX(NonProcessingDay) FROM @tmpHolidayTable) < DATEADD(yy,@ReportYears,GETDATE()))
		INSERT INTO @tmpHolidayTable SELECT MAX(NonProcessingDay) + 1, '' FROM  @tmpHolidayTable

--	UPDATE @tmpholidayTable set nonprocessingDate = SUBSTRING(Convert(varchar(32), NonProcessingDay, 101), 1, 10)
--	DELETE FROM @tmpHolidayTable where datepart(dw,nonprocessingDay) = 7  -- delete Sat,  Since Sunday is day 1, Sat is Day 7  
--  Now check the rundays, if Sunday is not a runday, remove Sunday, same with Friday.
	IF CHARINDEX('SUNDAY', @Rundays) != 0
		DELETE FROM @tmpHolidayTable WHERE DATEPART(dw,nonprocessingDay) = 1
	
	IF CHARINDEX('MONDAY', @RunDays) != 0
		DELETE FROM @tmpHolidayTable WHERE DATEPART(dw,nonprocessingDay) = 2
	
	IF CHARINDEX('TUESDAY', @Rundays) != 0
		DELETE FROM @tmpHolidayTable WHERE DATEPART(dw,nonprocessingDay) = 3
	
	IF CHARINDEX('WEDNESDAY', @RunDays) != 0
		DELETE FROM @tmpHolidayTable WHERE DATEPART(dw,nonprocessingDay) = 4

	IF CHARINDEX('THURSDAY', @Rundays) != 0
		DELETE FROM @tmpHolidayTable WHERE DATEPART(dw,nonprocessingDay) = 5
	
	IF CHARINDEX('FRIDAY', @RunDays) != 0
		DELETE FROM @tmpHolidayTable WHERE DATEPART(dw,nonprocessingDay) = 6

	IF @skip = 0  --  This means to process the day before a holiday
 		INSERT INTO @tmpholidaytable SELECT [Date], '' FROM Holidays					
	ELSE
		INSERT INTO @tmpholidaytable SELECT Dateadd(dd, -1, [Date]), '' FROM Holidays --WHERE [Date] in ( Select [Date] FROM Holidays )

	UPDATE @tmpholidayTable set nonprocessingDate = SUBSTRING(Convert(varchar(32), NonProcessingDay, 101), 1, 10)
	select * from @tmpholidaytable order by nonprocessingday

END

