CREATE PROCEDURE [dbo].[wc_PayeeAddMapping]
	@memberid	char(32),
	@payeeid	char(12),
	@payeelistid	char(12),	-- Must me provided to activate an existing USER_PAYEE record
	@payacct	char(32),
	@nameonacct	char(60),
	@createdatein datetime = '01/01/1900 00:00:00'	-- Optional: 30May1999 KKS: OFX Certification
AS
-- 
-- Scan the USER_PAYEE table to see a record already exists with the specified values.
-- If so, do nothing; otherwise add a new record.
-- 
DECLARE @rows int
DECLARE @createdate	datetime
DECLARE @payeelistiduse	char(12)
DECLARE @rc		int
DECLARE @MinDays	int
DECLARE @Type	int
DECLARE @PaymentMethod char(1)
DECLARE @Name varchar(32), @Addr1 varchar(32), @Addr2 varchar(32), @Zip varchar(10), @providerid int

SET @Type = 0
SELECT @PaymentMethod = PaymentMethod, @Name = Name, @Addr1 = Address1, @addr2 = Address2, @Zip = PostalCode, @ProviderID = ProviderID
	FROM Payee WITH (NOLOCK) 
WHERE PayeeID = @Payeeid

IF @ProviderID in (102, 103)
	SET @PaymentMethod = 'C'
ELSE
BEGIN
	SET @PaymentMethod = 'E'
	SET @Type = 1
END

-- KKS 30May1999: If an explicit createdate not given, 
-- then use current time; otherwise, use given one.
IF (@createdatein <> '01/01/1900 00:00:00') 
	SELECT @createdate = @createdatein
ELSE 
	SELECT @createdate = GetDate()

--SELECT @MinDays = MinDaysforPmtbyCheck from Configuration
SELECT @MinDays = 0
--  Check to see if user payee already exists
SELECT @rows=COUNT(*) 
	FROM User_Payee  with (nolock) 
WHERE PayeeListID=@payeelistid
IF (@@ERROR <> 0) GOTO on_error

IF (@rows = 0) 
BEGIN  --  If the user payee does not exists, then add it 
	EXEC @rc = GetID @id_type='PayeeListID', @id=@payeelistiduse OUTPUT
	If @rc < 0 
		GOTO on_error
	
	INSERT INTO User_Payee (PayeeListID, MemberID, PayeeID, UsersAccountAtPayee, NameOnAccount, CreatedOn, LastUpdatedOn, Active, PaymentMethod, MinDaystoPay) 
    		VALUES (@payeelistiduse, @memberid, @payeeid, @payacct, @nameonacct, @createdate, @createdate, 'Y', @PaymentMethod, @MinDays)
    	IF (@@ERROR <> 0) GOTO on_error

--  Validate payee before adding it to determine if it will go as check or electronic. 
IF @Type = 0
	BEGIN
		DECLARE @NewPaymentMethod char(1), @check char(1)
		SET @check = 'N'
		SELECT @check = ISNULL(ConfigValue,'N') FROM Configuration WHERE ConfigName = 'ValidatePayeeMethod'

		IF @check = 'Y'
		BEGIN
			EXEC @rc  = WRGBPS..ValidateNewPayee @Payeelistiduse, @Name, @Addr1, @Addr2, @Zip, @payacct, @NewPaymentMethod OUTPUT
			IF @rc  < 0
				GOTO on_error
		END
		ELSE
			SET @NewPaymentMethod = @PaymentMethod

		UPDATE User_Payee 
		   SET PaymentMethod = @NewPaymentMethod, 
             LastUpdatedOn = GetDate() 
         OUTPUT INSERTED.PayeeListID, INSERTED.MemberID, INSERTED.PayeeID, INSERTED.CreatedOn, 
               DELETED.LastUpdatedOn, INSERTED.UsersAccountAtPayee, INSERTED.NameOnAccount, 
               INSERTED.Active, INSERTED.MinDaystoPay, DELETED.PaymentMethod, 
               3, 'Member website edit: Modified Payee.', 'U', GETDATE(), ('HB-' + @memberid) 
         INTO User_Payee_Change_History (PayeeListID, MemberID, PayeeID, CreatedOn, 
               LastUpdatedOn, UsersAccountAtPayee, NameOnAccount, Active, MinDaystoPay, 
               PaymentMethod, User_Type, Reason, ModifyType, ModifyDate, ModifiedBy) 
		   WHERE PayeeListID = @PayeeListIDUse
	END
END
ELSE
   BEGIN
       SELECT @payeelistiduse = @payeelistid
       UPDATE User_Payee 
         SET Active='Y', 
             LastUpdatedOn = GetDate() 
         OUTPUT INSERTED.PayeeListID, INSERTED.MemberID, INSERTED.PayeeID, INSERTED.CreatedOn, 
               DELETED.LastUpdatedOn, INSERTED.UsersAccountAtPayee, INSERTED.NameOnAccount, 
               DELETED.Active, INSERTED.MinDaystoPay, INSERTED.PaymentMethod, 
               3, 'Member website edit: Modified Payee.', 'U', GETDATE(), ('HB-' + @memberid) 
         INTO User_Payee_Change_History (PayeeListID, MemberID, PayeeID, CreatedOn, 
               LastUpdatedOn, UsersAccountAtPayee, NameOnAccount, Active, MinDaystoPay, 
               PaymentMethod, User_Type, Reason, ModifyType, ModifyDate, ModifiedBy) 
         WHERE PayeeListID=@payeelistid
       IF (@@ERROR <> 0) GOTO on_error
   END
SELECT 'Status'=0, 'PayeeListID'=@payeelistiduse
RETURN (0)
on_error:
SELECT 'Status'=-1
RETURN (-1)

GO
