/*
 ==================================================================
  Stored Procedure Name: wc_PayeeMappingModify

  Description: 	

  Resources: 	Payee
		         User_Payee
		         User_Payee_Change_History
  ------------------------------------------------------------------
  Accepts:		@PayeeListID 	char(12),
			      @member_id 	char(32),
			      @payacct	char(32),
			      @nameonacct	char(60),
			      @user_type	tinyint,
			      @change_reason 	char(255)
			      @UpdatedateIn	datetime

  Returns:		returns: Status
   ==================================================================
   Created:  	?
   By:    		?
  ------------------------------------------------------------------
  Modifications: 
	05/10/06 HLiou  	 Added a check for the providers that are not 102 (non CU payee types), these payment methods must be 'E;
	06/15/06 GNordstrom  Added ouput clause to update statements and 
	                     removed "EXEC AddChangeHistory".
	11/10/10 HLiou       Added check for onus provider and make it Electronic.		

==================================================================
*/

CREATE PROCEDURE [dbo].[wc_PayeeMappingModify]
	         @payeelistid	char(12),
	         @member_id	   char(32),
	         @payacct	      char(32),
	         @nameonacct	   char(60),
	         @user_type		tinyint,	-- Required parameter, the user type that caused the change.
	         @change_reason	char(255),	-- Required parameter, the reason the change occurred.
	         @updatedatein  datetime = '01/01/1900 00:00:00'	-- Optional: 30May1999 KKS: OFX Certification
AS

DECLARE @updatedate	datetime
DECLARE  @oldpayacct char(32), 
         @oldnameonacct char(60), 
         @addr1 varchar(32), 
         @name varchar(32), 
         @addr2 varchar(32), 
         @zip varchar(11), 
	      @OldPaymentType char(1), 
	      @ProviderID int
SELECT @oldpayacct = NULL
SET @oldPaymentType = 'C'
SELECT @oldnameonacct = NULL
-- KKS 30May1999: If an explicit createdate not given, 
-- then use current time; otherwise, use given one.
IF (@updatedatein <> '01/01/1900 00:00:00') 
	SELECT @updatedate = @updatedatein
ELSE 
	SELECT @updatedate = GetDate()
-- 
-- Get the current UsersAccountAtPayee(PayAcct) value
-- 
SELECT   @OldPaymentType=u.PaymentMethod, 
         @oldpayacct = UsersAccountAtPayee, 
         @oldnameonacct = NameOnAccount, 
         @Name = Name, 
         @Addr1 = Address1, 
	      @addr2 = Address2, 
	      @Zip = PostalCode, 
	      @ProviderID = ProviderID
FROM User_Payee U with (nolock)
   INNER JOIN Payee p ON P.Payeeid = u.Payeeid 
WHERE MemberID=@member_id 
   And PayeeListID=@payeelistid
IF (@@ERROR <> 0) GOTO on_error

-- Check if the new value is the same as the current one.
-- If it is, then there's nothing to do
--
IF (@Payacct <> @OldPayacct)   -- OR (@nameonacct <> @oldnameonacct))
BEGIN
	DECLARE @NewPaymentMethod char(1), @rc int, @check char(1)
	SET @Check = 'N'
	SELECT @check = ISNULL(ConfigValue,'N') 
	   FROM Configuration 
	   WHERE ConfigName = 'ValidatePayeeMethod'

   --  Only validate payee method if it is turned on ValidatePayeeMethod = 'Y'
	IF @Providerid = 102  --  Only check if the original providerid is not a special provider
	   BEGIN
		   IF @check = 'Y'
		      BEGIN
			      EXEC @rc  = WRGBPS..ValidateNewPayee @Payeelistid, @Name, @Addr1, @Addr2, @Zip, @payacct, @NewPaymentMethod OUTPUT
			      IF @rc  < 0
				      GOTO on_error
		      END
		   ELSE
			   SET @NewPaymentMethod = @OldPaymentType
	   END
	ELSE
		SET @NewPaymentMethod = 'E'   -- All other providers are electronic payees


	UPDATE User_Payee 
	   SET   PaymentMethod = @NewPaymentMethod, 
	         UsersAccountAtPayee = @payacct, 
	         NameOnAccount= @nameonacct,
		      LastUpdatedOn = @updatedate, 
		      Active = 'Y' 
      OUTPUT INSERTED.PayeeListID, INSERTED.MemberID, INSERTED.PayeeID, INSERTED.CreatedOn, 
               DELETED.LastUpdatedOn, DELETED.UsersAccountAtPayee, DELETED.NameOnAccount, 
               DELETED.Active, INSERTED.MinDaystoPay, DELETED.PaymentMethod, 
               @user_type, @change_reason, 'U', GETDATE(), ('HB-' + @member_id) 
         INTO User_Payee_Change_History (PayeeListID, MemberID, PayeeID, CreatedOn, 
               LastUpdatedOn, UsersAccountAtPayee, NameOnAccount, Active, MinDaystoPay, 
               PaymentMethod, User_Type, Reason, ModifyType, ModifyDate, ModifiedBy) 
	WHERE PayeeListID = @PayeeListID 
	      and MemberID= @Member_ID

END
ELSE IF (@nameonacct <> @oldnameonacct)
	UPDATE User_Payee 
	   SET   UsersAccountAtPayee = @payacct, 
	         NameOnAccount= @nameonacct,
		      LastUpdatedOn = @updatedate, 
		      Active = 'Y' 
      OUTPUT INSERTED.PayeeListID, INSERTED.MemberID, INSERTED.PayeeID, INSERTED.CreatedOn, 
               DELETED.LastUpdatedOn, DELETED.UsersAccountAtPayee, DELETED.NameOnAccount, 
               DELETED.Active, INSERTED.MinDaystoPay, INSERTED.PaymentMethod, 
               @user_type, @change_reason, 'U', GETDATE(), ('HB-' + @member_id) 
         INTO User_Payee_Change_History (PayeeListID, MemberID, PayeeID, CreatedOn, 
               LastUpdatedOn, UsersAccountAtPayee, NameOnAccount, Active, MinDaystoPay, 
               PaymentMethod, User_Type, Reason, ModifyType, ModifyDate, ModifiedBy) 
	WHERE PayeeListID = @PayeeListID 
	      and MemberID= @Member_ID

Select 'Status'=0
RETURN (0)
on_error:
Select 'Status'=-1
RETURN (-1)

GO
