/*
 ==================================================================
  Stored Procedure Name: wc_PayeeModify

  Description: 	

  Resources: 	User_Payee
               Payee
               Provider
               User_Payee_Change_History
               Payee_Change_History
  ------------------------------------------------------------------
  Accepts:		@payeelistid      char(12),  -- Required parameter
               @memberid         char(32),  -- Required parameter
               @pmtmod_req       tinyint,       -- Required parameter
               @mod_name         char(32) = '',
               @mod_addr1        char(32) = '',
               @mod_addr2        char(32) = '',
               @mod_addr3        char(32) = '',
               @mod_city         char(32) = '',
               @mod_state        char(5)  = '',
               @mod_postal       char(11) = '',
               @mod_country      char(3)  = '',
               @mod_phone        char(32) = '',
               @user_type		   tinyint,	-- Required parameter, the user type that caused the change.
               @change_reason	   char(255),-- Required parameter, the reason the change occurred.
               @use_trans	      char(1)  = 'Y',
               @updatedatein     datetime = '01/01/1900 00:00:00'	-- Optional: 30May1999 KKS: OFX Certification

  Returns:		
   ==================================================================
   Created:  	?
   By:    		?
  ------------------------------------------------------------------
  Modifications: 
	06/15/06 GNordstrom  Changed the update to the payee table to be one
	                     update statement instead of multiple update statements.
	                     Added ouput clause to update statements and 
	                     removed "EXEC AddChangeHistory".
	05/11/07 GNordstrom  Modified so that if the default providers global payees 
	                     are edited by a member, first a search is done to see if
	                     the payee already exists as modified by the member and if 
	                     it does then the payee mapping is changed in the user_payee
	                     table and if it does not exist then a new payee is created 
	                     and the payee mapping is changed in the user_payee table.
	                     Also eliminated the use of the @pmtmod_req and the @use_trans
	                     parameters in the code but left these parameters in the 
	                     parameter list for backwards compatibility.
==================================================================
*/
CREATE PROCEDURE [dbo].[wc_PayeeModify]
	               @payeelistid      char(12),  -- Required parameter
	               @memberid         char(32),  -- Required parameter
	               @pmtmod_req       tinyint,       -- Required parameter
	               @mod_name         char(32) = '',
	               @mod_addr1        char(32) = '',
	               @mod_addr2        char(32) = '',
	               @mod_addr3        char(32) = '',
	               @mod_city         char(32) = '',
	               @mod_state        char(5)  = '',
	               @mod_postal       char(11) = '',
	               @mod_country      char(3)  = '',
	               @mod_phone        char(32) = '',
	               @user_type		   tinyint,	-- Required parameter, the user type that caused the change.
	               @change_reason	   char(255),-- Required parameter, the reason the change occurred.
	               @use_trans	      char(1)  = 'Y',
	               @updatedatein     datetime = '01/01/1900 00:00:00'	-- Optional: 30May1999 KKS: OFX Certification
AS
-- 
-- Local variables
--
DECLARE @payeeid		   varchar(12)	-- Existing PayeeID value
DECLARE @nameval		   varchar(32)	-- PAYEE.Name column
DECLARE @scopeval		   varchar(10)	-- PAYEE.Scope column
DECLARE @others			int			-- How many other members are using the Payee?
DECLARE @def_provider	int			-- Default BP Provider ID (for new Payee records only)
DECLARE @new_payeeid    varchar(12)
DECLARE @rc			      int
DECLARE @updatedate	   datetime  
DECLARE @Default        varchar(1) 

-- KKS 30May1999: If an explicit createdate not given, 
-- then use current time; otherwise, use given one.
IF (@updatedatein <> '01/01/1900 00:00:00') 
   SELECT @updatedate = @updatedatein
ELSE 
   SELECT @updatedate = GETDATE()

-- First, get the PayeeID from the USER_PAYEE table, and see if it's a valid one.
BEGIN TRY 
   SELECT @payeeid = PayeeID 
      FROM User_Payee WITH (NOLOCK) 
      WHERE PayeeListID = @payeelistid

   SELECT @nameval = p.Name, @scopeval = p.Scope, @Default = pr.DefaultProvider 
      FROM Payee p WITH (NOLOCK) INNER JOIN Provider pr WITH (NOLOCK) ON p.ProviderID = pr.ProviderID 
      WHERE p.PayeeID = @payeeid

   IF (@scopeval IS NOT NULL)
      BEGIN 
         --If the payee's provider is the default provider
         IF @Default = 'Y' 
            BEGIN 
               --If the payee scope is USER then determine if others are using this payee.
               IF UPPER(@scopeval) = 'USER'
                  SELECT @others = COUNT(*) 
                     FROM User_Payee  WITH (NOLOCK) 
                     WHERE PayeeID = @payeeid 
                        AND MemberID <> @memberid

               BEGIN TRANSACTION
               
               --If the payee scope is USER and others are using this payee OR the payee scope is GLOBAL.
               --Check for an existing match in the payee table to the modified payee information.
               IF ((UPPER(@scopeval) = 'USER') And (@others > 0) OR (UPPER(@scopeval) = 'GLOBAL'))
                  BEGIN
                     --Get the payee id of any existing payee that 
                     --matches the modified payee information passed in.
                     SELECT @new_payeeid = PayeeID 
                        FROM Payee 
                        WHERE UPPER([Name]) = UPPER(@mod_name) 
                           AND UPPER(Address1) = UPPER(@mod_addr1) 
                           AND UPPER(Address2) = UPPER(@mod_addr2) 
                           AND UPPER(City) = UPPER(@mod_city) 
                           AND UPPER(State) = UPPER(@mod_state) 
                           AND PostalCode = @mod_postal  
                           AND Phone = @mod_phone

                     --If there is no match then add a new payee record.
                     IF @new_payeeid IS NULL
                        BEGIN
                        --INSERT NEW RECORD
                           SELECT @def_provider = ProviderID 
                              FROM Provider WITH (NOLOCK) 
                              WHERE DefaultProvider = 'Y'
                        
                           --Get new PayeeID
                           EXEC @rc = GetID @id_type = 'PayeeID', @id = @new_payeeid OUTPUT   
                           IF @rc >= 0 
                              --Create the new payee record.
                              INSERT INTO Payee (Name, Address1, Address2, Address3, City, State, PostalCode, Country, Phone,
                                                PayeeID, PayeeAliasID, ProviderID, Scope, LastUpdatedOn, LastUpdatedBy,
                                                BankID, BranchID, AccountID, AccountType, AccountKey )
                              VALUES (@mod_name, @mod_addr1, @mod_addr2, '', @mod_city, @mod_state, @mod_postal,
                                       'USA', @mod_phone, @new_payeeid, ' ', @def_provider, 'USER',  @updatedate, @memberid,
                                       '', '', '', '', '')
                           ELSE
                              RAISERROR('New PayeeID could not be obtained.', 16, 1)
                        END
                                 
                        -- Modify USER_PAYEE to point to the Payee record.
                        UPDATE User_Payee 
                           SET   PayeeID = @new_payeeid, 
                                 LastUpdatedOn = @updatedate 
                           OUTPUT INSERTED.PayeeListID, INSERTED.MemberID, DELETED.PayeeID, INSERTED.CreatedOn, 
                                    DELETED.LastUpdatedOn, INSERTED.UsersAccountAtPayee, INSERTED.NameOnAccount, 
                                    INSERTED.Active, INSERTED.MinDaystoPay, INSERTED.PaymentMethod, 
                                    @user_type, @change_reason, 'U', GETDATE(), ('HB-' + @memberid)  
                              INTO User_Payee_Change_History (PayeeListID, MemberID, PayeeID, CreatedOn, 
                                    LastUpdatedOn, UsersAccountAtPayee, NameOnAccount, Active, MinDaystoPay, 
                                    PaymentMethod, User_Type, Reason, ModifyType, ModifyDate, ModifiedBy) 
                        WHERE PayeeListID = @payeelistid

                        SELECT "Status" = 3, "PayeeID" = @new_payeeid
                  END   
               ELSE IF ((UPPER(@scopeval) = 'USER') And (@others = 0))
               --If the payee being edited is a USER scope and no other users 
               --are using it just modify the existing payee record.
                  BEGIN
                     UPDATE Payee 
                        SET   [Name] = @mod_name, 
                              Address1 = @mod_addr1, 
                              Address2 = @mod_addr2, 
                              Address3 = @mod_addr3, 
                              City = @mod_city, 
                              State = @mod_state, 
                              PostalCode = @mod_postal, 
                              Country = @mod_country, 
                              Phone = @mod_phone, 
                              LastUpdatedBy=@memberid, 
                              LastUpdatedOn=@updatedate 
                        OUTPUT INSERTED.PayeeID, INSERTED.PayeeAliasID, INSERTED.ProviderID, 
                               DELETED.[Name], DELETED.Address1, DELETED.Address2, DELETED.Address3, 
                               DELETED.City, DELETED.State, DELETED.PostalCode, DELETED.Country, 
                               DELETED.Phone, INSERTED.Scope, INSERTED.MinDaysToPay, 
                               DELETED.LastUpdatedOn, DELETED.LastUpdatedBy, INSERTED.BankID, 
                               INSERTED.BranchID, INSERTED.AccountID, INSERTED.AccountType, 
                               INSERTED.PaymentMethod, INSERTED.AccountKey, INSERTED.EntryDate, 
                               INSERTED.PayeeType, INSERTED.BillerKey, INSERTED.AddressKey, 
                               @user_type, @change_reason, 'U', GETDATE(),  ('HB-' + @memberid)
                           INTO Payee_Change_History 
                              (PayeeID, PayeeAliasID, ProviderID, [Name], Address1, Address2, 
                              Address3, City, State, PostalCode, Country, Phone, Scope, MinDaysToPay, 
                              LastUpdatedOn, LastUpdatedBy, BankID, BranchID, AccountID, AccountType, 
                              PaymentMethod, AccountKey, EntryDate, PayeeType, BillerKey, AddressKey,	
                              User_Type, Reason, ModifyType, ModifyDate, ModifiedBy) 
                     WHERE PayeeID = @payeeid
                     
                     SELECT "Status" = 0, "PayeeID" = @payeeid
                  END
                   
               COMMIT TRANSACTION
               RETURN (0)
            END
         ELSE
            --@Default = 'N' -- Payee's provider is not the default provider.
            BEGIN 
               PRINT 'MemberID is not allowed to modify this Payee'
               SELECT "Status" = 2
               RETURN (2)
            END
      END
   ELSE
   --@scopeval IS NULL
      BEGIN
         PRINT 'Specified Payee could not be found'
         SELECT "Status" = 1
         RETURN (1)
      END
END TRY
BEGIN CATCH
   PRINT 'Error occurred: ' + ERROR_MESSAGE()
   IF (@@TRANCOUNT > 0) OR ((XACT_STATE()) = -1)
      ROLLBACK TRANSACTION
      
   SELECT "Status" = -1
   RETURN (-1)
END CATCH






GO
