CREATE PROCEDURE [dbo].[wc_PaymentListSyncFilterNew]
                           @MemberId char(32),
                           @PayeeListID varchar(12),
                           @StartDate datetime,
                           @EndDate datetime,
                           @AmountFrom int,
                           @AmountTo int 
AS
if (@StartDate is NULL) set @StartDate = '01/01/1970'
if (@EndDate is NULL) set @EndDate = '01/01/2050'
if (@AmountFrom is NULL) set @AmountFrom = 0
if (@AmountTo is NULL) set @AmountTo = 90000000

if (@PayeeListId ='All') 
   BEGIN
      --Non-cancelled payments
      SELECT   'PH' as RowType, 
               RTRIM(ph.PaymentID) as PaymentID, 
               RTRIM(ph.PayeeListID) as PayeeListID,
               RTRIM(ph.Account) as Account, 
               ph.Amount as Amount, 
               RTRIM(ISNULL(s.friendlyname,' ')) as StatusCode, 
               RTRIM(ph.Memo) as Memo, 
               RTRIM(ph.BillReference) as BillRefInfo,
               RTRIM(ph.UsersAccountAtPayee) as PayAcct, 
               RTRIM(ph.NameOnAccount) as NameOnAcct, 
               RTRIM(u.PayeeID) as PayeeID,
               RTRIM(ph.RecPaymentID) as RecPaymentID, 
               ph.ProcessedDate,
               RTRIM(e.Name) as Name, 
               RTRIM(e.Scope) as Scope, 
               u.MinDaysToPay as MinDaysToPay, 
               m.Description as PaymentMethod,
               ph.WillProcessDate as WillProcessDate,
               wc_Nicknames.NickName,
               COALESCE( wc_NickNames.NickName, e.Name) as SortName,
               RTRIM(ph.CheckNum) as  CheckNumber, 
               cc.ClearedDate as ClearedDate, 
               ph.DueDate, 
               ISNULL(ph.TrsfSource, '') as TrsfSource,
			   ph.DeliveryDate   
         FROM Payment_history ph WITH (NOLOCK) 
               INNER JOIN User_Payee u  WITH (NOLOCK) ON ph.PayeeListID=u.PayeeListID and ph.MemberId=u.MemberId
               INNER JOIN Payee e  WITH (NOLOCK) ON u.PayeeID=e.PayeeID
               INNER JOIN Status_Code s WITH (NOLOCK) ON s.statuscode = ph.statuscode
               LEFT JOIN Payment_Method m  WITH (NOLOCK) ON m.PaymentMethod = ph.PaymentMethod
               LEFT JOIN Payment_Clear cc WITH (NOLOCK) ON ph.PaymentID = cc.PaymentID
               LEFT OUTER JOIN wc_NickNames  WITH (NOLOCK) ON u.PayeeListID = wc_NickNames.PayeeListID and u.MemberID = wc_NickNames.MemberID
         WHERE ph.MemberId = @MemberId AND 
               DATEDIFF(day, ph.ProcessedDate, @StartDate) <= 0 AND
               DATEDIFF(day, ph.ProcessedDate, @EndDate) >= 0 AND 
               ph.CancelledDate IS NULL AND 
               ph.Amount >= @AmountFrom AND
               ph.Amount <= @AmountTo
      UNION 
      --Cancelled payments
      SELECT   'PH' as RowType, 
               RTRIM(ph.PaymentID) as PaymentID, 
               RTRIM(ph.PayeeListID) as PayeeListID,
               RTRIM(ph.Account) as Account, 
               ph.Amount as Amount, 
               RTRIM(ISNULL(s.friendlyname,' ')) as StatusCode, 
               RTRIM(ph.Memo) as Memo, 
               RTRIM(ph.BillReference) as BillRefInfo,
               RTRIM(ph.UsersAccountAtPayee) as PayAcct, 
               RTRIM(ph.NameOnAccount) as NameOnAcct, 
               RTRIM(u.PayeeID) as PayeeID,
               RTRIM(ph.RecPaymentID) as RecPaymentID, 
               ph.ProcessedDate,
               RTRIM(e.Name) as Name, 
               RTRIM(e.Scope) as Scope, 
               u.MinDaysToPay as MinDaysToPay, 
               m.Description as PaymentMethod,
               ph.WillProcessDate as WillProcessDate,
               wc_Nicknames.NickName,
               COALESCE( wc_NickNames.NickName, e.Name) as SortName,
               RTRIM(ph.CheckNum) as  CheckNumber, 
               cc.ClearedDate as ClearedDate, 
               ph.DueDate, 
               ISNULL(ph.TrsfSource, '') as TrsfSource ,
			   ph.DeliveryDate    
         FROM Payment_history ph WITH (NOLOCK) 
               INNER JOIN User_Payee u  WITH (NOLOCK) ON ph.PayeeListID=u.PayeeListID and ph.MemberId=u.MemberId
               INNER JOIN Payee e  WITH (NOLOCK) ON u.PayeeID=e.PayeeID
               INNER JOIN Status_Code s WITH (NOLOCK) ON s.statuscode = ph.statuscode
               LEFT JOIN Payment_Method m  WITH (NOLOCK) ON m.PaymentMethod = ph.PaymentMethod
               LEFT JOIN Payment_Clear cc WITH (NOLOCK) ON ph.PaymentID = cc.PaymentID
               LEFT OUTER JOIN wc_NickNames  WITH (NOLOCK) ON u.PayeeListID = wc_NickNames.PayeeListID and u.MemberID = wc_NickNames.MemberID
         WHERE ph.MemberId = @MemberId AND 
               DATEDIFF(day, ph.CancelledDate, @StartDate) <= 0 AND
               DATEDIFF(day, ph.CancelledDate, @EndDate) >= 0 AND 
               ph.CancelledDate IS NOT NULL AND 
               ph.Amount >= @AmountFrom AND
               ph.Amount <= @AmountTo  
      UNION
      --Payments being processed by the generator but not in payment_history yet.
      SELECT   'PH' as RowType, 
               RTRIM(p.PaymentID) as PaymentID, 
               RTRIM(p.PayeeListID) as PayeeListID,
               RTRIM(p.Account) as Account, 
               p.amount as Amount, 
               RTRIM( ISNULL(s.friendlyname,' ')) as StatusCode, 
               RTRIM(p.Memo) as Memo, 
               RTRIM(p.BillReference) as BillRefInfo,
               RTRIM(u.UsersAccountAtPayee) as PayAcct, 
               RTRIM(u.NameOnAccount) as NameOnAcct, 
               RTRIM(u.PayeeID) as PayeeID,
               RTRIM(p.RecPaymentID) as RecPaymentID, 
               p.WillProcessDate as ProcessedDate,        
               RTRIM(e.Name) as Name, 
               RTRIM(e.Scope) as Scope, 
               ISNULL(e.MinDaysToPay,0) as MinDaysToPay, 
               ISNULL(m.Description,' ') as PaymentMethod,
               p.WillProcessDate as WillProcessDate,
               wc_NickNames.NickName, 
               COALESCE( wc_NickNames.NickName, e.Name) as SortName,
               '' as CheckNumber,
               cc.ClearedDate as ClearedDate, 
               p.DueDate, 
               ISNULL(p.TrsfSource, '') as TrsfSource,
			   p.DeliveryDate       
         FROM Payment p WITH (NOLOCK) 
               INNER JOIN User_Payee u  WITH (NOLOCK) ON p.PayeeListID=u.PayeeListID and p.MemberId=u.MemberId
               INNER JOIN Payee e  WITH (NOLOCK) ON u.PayeeID=e.PayeeID
               INNER JOIN Status_Code s WITH (NOLOCK) ON s.statuscode = p.statuscode
               LEFT JOIN Payment_Method m WITH (NOLOCK)  ON m.PaymentMethod = u.PaymentMethod
               LEFT JOIN Payment_Clear cc WITH (NOLOCK) ON p.PaymentID = cc.PaymentID
               LEFT OUTER JOIN wc_NickNames  WITH (NOLOCK) ON u.PayeeListID = wc_NickNames.PayeeListID and @MemberID = wc_NickNames.MemberID
         WHERE p.MemberId = @MemberId AND
               p.StatusCode = 101 AND 
               DATEDIFF(day, p.WillProcessDate, @StartDate) <= 0 AND
               DATEDIFF(day, p.WillProcessDate, @EndDate) >= 0 AND 
               p.Amount >= @AmountFrom AND
               p.Amount <= @AmountTo
         ORDER BY ProcessedDate
         
      if (@@error <> 0) goto on_error
   END
ELSE 
   BEGIN
      --Non-cancelled payments
      SELECT   'PH' as RowType, 
               RTRIM(ph.PaymentID) as PaymentID, 
               RTRIM(ph.PayeeListID) as PayeeListID,
               RTRIM(ph.Account) as Account, 
               ph.Amount as Amount, 
               RTRIM(ISNULL(s.friendlyname,' ')) as StatusCode, 
               RTRIM(ph.Memo) as Memo, 
               RTRIM(ph.BillReference) as BillRefInfo,
               RTRIM(ph.UsersAccountAtPayee) as PayAcct, 
               RTRIM(ph.NameOnAccount) as NameOnAcct, 
               RTRIM(u.PayeeID) as PayeeID,
               RTRIM(ph.RecPaymentID) as RecPaymentID, 
               ph.ProcessedDate,
               RTRIM(e.Name) as Name, 
               RTRIM(e.Scope) as Scope, 
               u.MinDaysToPay as MinDaysToPay, 
               m.Description as PaymentMethod,
               ph.WillProcessDate as WillProcessDate,
               wc_Nicknames.NickName,
               COALESCE( wc_NickNames.NickName, e.Name) as SortName,
               RTRIM(ph.CheckNum) as  CheckNumber, 
               cc.ClearedDate as ClearedDate, 
               ph.DueDate, 
               ISNULL(ph.TrsfSource, '') as TrsfSource,
			   ph.DeliveryDate     
         FROM Payment_history ph WITH (NOLOCK) 
               INNER JOIN User_Payee u  WITH (NOLOCK) ON ph.PayeeListID=u.PayeeListID and ph.MemberId=u.MemberId
               INNER JOIN Payee e  WITH (NOLOCK) ON u.PayeeID=e.PayeeID
               INNER JOIN Status_Code s WITH (NOLOCK) ON s.statuscode = ph.statuscode
               LEFT JOIN Payment_Clear cc WITH (NOLOCK) ON ph.PaymentID = cc.PaymentID
               LEFT JOIN Payment_Method m  WITH (NOLOCK) ON m.PaymentMethod = ph.PaymentMethod
               LEFT OUTER JOIN wc_NickNames  WITH (NOLOCK) ON u.PayeeListID = wc_NickNames.PayeeListID and u.MemberID = wc_NickNames.MemberID
         WHERE ph.MemberId = @MemberId AND 
               DATEDIFF(day, ph.ProcessedDate, @StartDate) <= 0 AND
               DATEDIFF(day, ph.ProcessedDate, @EndDate) >= 0 AND
               ph.CancelledDate IS NULL AND 
               ph.Amount >= @AmountFrom AND
               ph.Amount <= @AmountTo AND 
               ph.PayeeListID = @PayeeListID
      UNION
      --Cancelled payments
      SELECT   'PH' as RowType, 
               RTRIM(ph.PaymentID) as PaymentID, 
               RTRIM(ph.PayeeListID) as PayeeListID,
               RTRIM(ph.Account) as Account, 
               ph.Amount as Amount, 
               RTRIM(ISNULL(s.friendlyname,' ')) as StatusCode, 
               RTRIM(ph.Memo) as Memo, 
               RTRIM(ph.BillReference) as BillRefInfo,
               RTRIM(ph.UsersAccountAtPayee) as PayAcct, 
               RTRIM(ph.NameOnAccount) as NameOnAcct, 
               RTRIM(u.PayeeID) as PayeeID,
               RTRIM(ph.RecPaymentID) as RecPaymentID, 
               ph.ProcessedDate,
               RTRIM(e.Name) as Name, 
               RTRIM(e.Scope) as Scope, 
               u.MinDaysToPay as MinDaysToPay, 
               m.Description as PaymentMethod,
               ph.WillProcessDate as WillProcessDate,
               wc_Nicknames.NickName,
               COALESCE( wc_NickNames.NickName, e.Name) as SortName,
               RTRIM(ph.CheckNum) as  CheckNumber, 
               cc.ClearedDate as ClearedDate, 
               ph.DueDate, 
               ISNULL(ph.TrsfSource, '') as TrsfSource,
			   ph.DeliveryDate     
         FROM Payment_history ph WITH (NOLOCK) 
               INNER JOIN User_Payee u  WITH (NOLOCK) ON ph.PayeeListID=u.PayeeListID and ph.MemberId=u.MemberId
               INNER JOIN Payee e  WITH (NOLOCK) ON u.PayeeID=e.PayeeID
               INNER JOIN Status_Code s WITH (NOLOCK) ON s.statuscode = ph.statuscode
               LEFT JOIN Payment_Clear cc WITH (NOLOCK) ON ph.PaymentID = cc.PaymentID
               LEFT JOIN Payment_Method m  WITH (NOLOCK) ON m.PaymentMethod = ph.PaymentMethod
               LEFT OUTER JOIN wc_NickNames  WITH (NOLOCK) ON u.PayeeListID = wc_NickNames.PayeeListID and u.MemberID = wc_NickNames.MemberID
         WHERE ph.MemberId = @MemberId AND 
               DATEDIFF(day, ph.CancelledDate, @StartDate) <= 0 AND
               DATEDIFF(day, ph.CancelledDate, @EndDate) >= 0 AND
               ph.CancelledDate IS NOT NULL AND 
               ph.Amount >= @AmountFrom AND
               ph.Amount <= @AmountTo AND 
               ph.PayeeListID = @PayeeListID      
      UNION
      --Payments being processed by the generator but not in payment_history yet.
      SELECT   'PH' as RowType, 
               RTRIM(p.PaymentID) as PaymentID, 
               RTRIM(p.PayeeListID) as PayeeListID,
               RTRIM(p.Account) as Account, 
               p.amount as Amount, 
               RTRIM( ISNULL(s.friendlyname,' ')) as StatusCode, 
               RTRIM(p.Memo) as Memo, 
               RTRIM(p.BillReference) as BillRefInfo,
               RTRIM(u.UsersAccountAtPayee) as PayAcct, 
               RTRIM(u.NameOnAccount) as NameOnAcct, 
               RTRIM(u.PayeeID) as PayeeID,
               RTRIM(p.RecPaymentID) as RecPaymentID, 
               p.WillProcessDate as ProcessedDate,        
               RTRIM(e.Name) as Name, 
               RTRIM(e.Scope) as Scope, 
               ISNULL(e.MinDaysToPay,0) as MinDaysToPay, 
               ISNULL(m.Description,' ') as PaymentMethod,
               p.WillProcessDate as WillProcessDate,
               wc_NickNames.NickName, 
               COALESCE( wc_NickNames.NickName, e.Name) as SortName,
               '' as CheckNumber,
               cc.ClearedDate as ClearedDate, 
               p.DueDate, 
               ISNULL(p.TrsfSource, '') as TrsfSource,
			   p.DeliveryDate      
         FROM Payment p WITH (NOLOCK) 
               INNER JOIN User_Payee u  WITH (NOLOCK) ON p.PayeeListID=u.PayeeListID and p.MemberId=u.MemberId
               INNER JOIN Payee e  WITH (NOLOCK) ON u.PayeeID=e.PayeeID
               INNER JOIN Status_Code s WITH (NOLOCK) ON s.statuscode = p.statuscode
               LEFT JOIN Payment_Method m WITH (NOLOCK)  ON m.PaymentMethod = u.PaymentMethod
               LEFT JOIN Payment_Clear cc WITH (NOLOCK) ON p.PaymentID = cc.PaymentID
               LEFT OUTER JOIN wc_NickNames  WITH (NOLOCK) ON u.PayeeListID = wc_NickNames.PayeeListID and @MemberID = wc_NickNames.MemberID
         WHERE p.MemberId = @MemberId AND
               p.StatusCode = 101 AND 
               DATEDIFF(day, p.WillProcessDate, @StartDate) <= 0 AND
               DATEDIFF(day, p.WillProcessDate, @EndDate) >= 0 AND 
               p.Amount >= @AmountFrom AND
               p.Amount <= @AmountTo AND 
               p.PayeeListID = @PayeeListID               
         ORDER BY ProcessedDate

      if (@@error <> 0) goto on_error
   END
   
return(0)
on_error:
return(-1)



