	            @user_type		   tinyint,	         -- Required parameter, the user type that caused the change.
	            @change_reason	   char(255),	      -- Required parameter, the reason the change occurred.
	            @willprocess_date	smalldatetime = NULL,
	            @TrsfSource          varchar(22) = NULL   

  Returns:		
   ==================================================================
   Created:  	?
   By:    		?
  ------------------------------------------------------------------
  Modifications: 
	06/15/2006  GNordstrom  Made the updates to the table one update statement and 
	                        added the ouput clause to the update statement.  Removed 
	                        the transaction since there is only one update statement 
	                        now and removed the "exec AddChangeHistory".
	01/24/2007  GNordstrom  Modified to update the DueDate field.
	08/31/2007  GNordstrom  Added new parameter @TrsfSource to update the TrsfSource field.
	04/30/2008  GNordstrom  Changed name from PaymentModify to wc_PaymentModify.  Eliminated
	                        exec of PayeeMappingModify sp and check of Payment_History table.
	                        Modified code to not allow edit of a payment with a status code != 100 and 
	                        restructured code to eliminate goto statements and to use try catch statements.
     4/28/2013   HLiou	   Added Delivery Date to payment history

==================================================================
*/
CREATE PROCEDURE [dbo].[wc_PaymentModify]
	            @member_id		   char(32),	-- Required parameter
	            @pmt_id			   char(10),	-- Required parameter
	            @payee_list_id	   char(12)  = NULL,
	            @account		      char(22)  = NULL,
	            @amount			   int       = NULL,
	            @memo			      char(255) = NULL,
	            @bill_ref		   char(80)  = NULL,
	            @due_date		   smalldatetime = NULL,
	            @srcappl		      char(10) = NULL,
	            @payacct		      char(32) = NULL,
	            @nameonacct		   char(60) = NULL,
	            @user_type		   tinyint,	         -- Required parameter, the user type that caused the change.
	            @change_reason	   char(255),	      -- Required parameter, the reason the change occurred.
	            @willprocess_date	smalldatetime = NULL,
	            @TrsfSource       varchar(22) = NULL,
			    @DeliveryDate		smalldatetime = NULL   
AS

DECLARE @StatusCode int

BEGIN TRY
   SELECT @StatusCode = StatusCode  
      FROM Payment WITH (NOLOCK) 
      WHERE MemberID = @member_id 
         AND PaymentID = @pmt_id
         
   IF (@StatusCode = 100)
   BEGIN
      -- Make the desired modification(s)
      UPDATE Payment 
         SET Account = @account, 
            Amount = @amount, 
            PayeeListID = @payee_list_id, 
            SourceApplication = @srcappl, 
            Memo = @memo, 
            BillReference = @bill_ref, 
            WillProcessDate = @willprocess_date, 
            DueDate  =  @due_date, 
            LastUpdateDateTime = GETDATE(), 
            TrsfSource = @TrsfSource,
			DeliveryDate = @DeliveryDate   
         OUTPUT INSERTED.PaymentID, DELETED.PayeeListID, DELETED.Account, DELETED.Amount, 
               DELETED.WillProcessDate, INSERTED.RecPaymentID, INSERTED.StatusCode, 
               DELETED.Memo, DELETED.BillReference, DELETED.LastUpdateDateTime, 
               INSERTED.MemberID, DELETED.SourceApplication, INSERTED.EntryDate, 
               DELETED.DueDate, @user_type, @change_reason, 'U', 
               GETDATE(), ('HB-' + @member_id), DELETED.TrsfSource 
            INTO Payment_Change_History 
               (PaymentID, PayeeListID, Account, Amount, WillProcessDate, RecPaymentID, 
               StatusCode, Memo, BillReference, LastUpdateDateTime, MemberID, 
               SourceApplication, EntryDate, DueDate, User_Type, Reason, ModifyType, 
               ModifyDate, ModifiedBy, TrsfSource)  
         WHERE MemberID = @member_id 
            AND PaymentID = @pmt_id
         
      SELECT 0 as Status
      RETURN 0
   END
   ELSE
   BEGIN
      SELECT @StatusCode as Status 
      RETURN -1      
   END
END TRY
BEGIN CATCH
   SELECT -1 as Status 
   RETURN -1
END CATCH



GO
