CREATE PROCEDURE [dbo].[wc_RecPmtCreate]
	                  @payeelistid	char(12),
	                  @memberid	char(32),
	                  @account	char(22),
	                  @amount		int,		-- in cents, not dollars!!
	                  @initial_amt	int,
	                  @final_amt	int,
	                  @numpmts	smallint,
	                  @duedate	char(50),	
	                  @frequency	char(15),
	                  @srcappl	char(10),
	                  @payacct	char(32),
	                  @nameonacct	char(60),
	                  @user_type	tinyint,
	                  @createdate datetime = '01/01/1900 00:00:00',
	                  @Memo	char(50) = '', 	-- optional, default to current KKS 06April1999
	                  @TrsfSource varchar(22) = NULL,
					  @DeliveryDate datetime = NULL 
AS
--
-- Local variables
--
DECLARE @date_to_use datetime
DECLARE @recpmtid	char(10)
DECLARE @rc		int
--
-- Create a new Recurring_Payment record.
--
IF (@createdate <> '01/01/1900 00:00:00') 
   BEGIN 
	   SELECT @date_to_use = @createdate
   END
ELSE 
   BEGIN
	   SELECT @date_to_use = GetDate()
   END
   
EXEC @rc = GetID @id_type='RecPaymentID', @id=@recpmtid	OUTPUT --KKS 17Sep1999 - ID Dispenser replacement
if @rc < 0 GOTO on_error

DECLARE @DateType varchar(25), 
         @ProcessDate smalldatetime
         
--Get the payment model from the configuration table
SELECT @DateType = ConfigValue 
   FROM Configuration 
   WHERE UPPER(ConfigName) = 'PAYMENTMODEL'

IF UPPER(@DateType) = 'DUE'
   EXEC dbo.any_CalculateProcessDate @duedate, @payeelistid, @ProcessDate OUTPUT 
ELSE
   SET @ProcessDate = @duedate 

INSERT INTO Recurring_Payment
	(RecPaymentID, PayeeListID, MemberID, Account, Amount, InitialAmount, FinalAmount, 
	 NumPayments, InitialDueDate, Frequency, LastProcessedDate, LastUpdateDateTime, Active,
	 SourceApplication, NextProcessDate, PaymentsProcessed, Memo, NextDueDate, TrsfSource, ProcessDay,
	 DeliveryDate)
VALUES
	(@recpmtid, @payeelistid, @memberid, @account, @amount, @initial_amt, @final_amt,
	 @numpmts, @ProcessDate, @frequency, NULL, @date_to_use, 'Y',
	 @srcappl, @ProcessDate, 0, @Memo, @duedate, @TrsfSource, DATEPART(d, @ProcessDate), @DeliveryDate)
if (@@ERROR <> 0) GOTO on_error
--
-- Update the UsersAccountAtPayee field
--
DECLARE @return_status	int
exec @return_status=PayeeMappingModify @payeelistid,@memberid,@payacct,@nameonacct,@user_type,'RecPmtCreate'
if (@return_status <> 0) GOTO on_error
SELECT 'RecPaymentID'=@recpmtid, 'Status'=0
RETURN (0)
on_error:
SELECT "Status"=-1
RETURN (-1)






GO
