--
IF NOT EXISTS (SELECT RecPaymentID FROM Recurring_Payment with (nolock) WHERE RecPaymentID = @RecPaymentID and PayeeListID = @PayeeListID and MemberID = @MemberID)
	GOTO no_match

DECLARE  @DateType varchar(25), 
         @ProcessDate smalldatetime
         
--Get the payment model from the configuration table
SELECT @DateType = ConfigValue 
   FROM Configuration 
   WHERE UPPER(ConfigName) = 'PAYMENTMODEL'

IF UPPER(@DateType) = 'DUE' AND  @newduedate != '12/31/2050'
   EXEC dbo.any_CalculateProcessDate @newduedate, @PayeeListId, @ProcessDate OUTPUT 
