CREATE PROCEDURE [dbo].[wc_RecPmtSchemaConvert]
@RecPaymentID char(10)
AS

DECLARE @lastProcessedDate 	smalldatetime
DECLARE @frequency		char(15)
Declare @MemberID as char(10)


SELECT @lastProcessedDate = lastProcessedDate, @Frequency=Frequency
FROM recurring_payment  WITH (NOLOCK) 
WHERE  Active = 'Y' and @RecPaymentID = RecPaymentID
if (@@error <> 0) goto on_error

DECLARE @tempDate		smalldatetime
DECLARE @i			int
DECLARE @initialDueDate 	smalldatetime
DECLARE @numPayments	smallint
DECLARE @minDaysToPay	smallint
DECLARE @paymentsLeft	smallint
DECLARE @COUNTER		int
DECLARE @npmt 		int


If @RecPaymentID = '' 
	DECLARE temp_cursor SCROLL CURSOR 
  	FOR SELECT RecPaymentID, InitialDueDate, LastProcessedDate, NumPayments, Frequency FROM Recurring_Payment  WITH (NOLOCK)  WHERE  Active = 'Y' 
else
	DECLARE temp_cursor SCROLL CURSOR 
  	FOR SELECT RecPaymentID, InitialDueDate, LastProcessedDate, NumPayments, Frequency FROM Recurring_Payment  WITH (NOLOCK)  WHERE  Active = 'Y' and @RecPaymentID = RecPaymentID

OPEN temp_cursor

FETCH FIRST FROM temp_cursor INTO @recPaymentID, @initialDueDate, @lastProcessedDate, @numPayments, @frequency
WHILE ( @@FETCH_STATUS = 0) 
BEGIN
	SELECT @i = 0
	SELECT @npmt = 0
	SELECT @tempDate = @initialDueDate

	WHILE (@tempDate <= @lastProcessedDate) AND (@npmt <= @numPayments)
	BEGIN 
		--print @TempDate
		SELECT @tempDate =
		  CASE @frequency
  			WHEN 'WEEKLY'   THEN DATEADD(week,1,@tempDate)
  			WHEN 'BIWEEKLY'  THEN DATEADD(week,2,@tempDate)
 			 WHEN 'FOURWEEKS'  THEN DATEADD(week,4,@tempDate)
 			 WHEN 'MONTHLY'   THEN DATEADD(month,1,@tempDate)
  			WHEN 'BIMONTHLY'  THEN DATEADD(month,2,@tempDate)
 			 WHEN 'SEMIANNUALLY' THEN DATEADD(month,6,@tempDate)
  			WHEN 'QUARTERLY'  THEN DATEADD(quarter,1,@tempDate)
  			WHEN 'ANNUALLY'  THEN DATEADD(year,1,@tempDate)  
  		END
  		SELECT @npmt = @npmt + 1
	 END
        
	--
	-- ...now tempDate is the next payment date.
	SELECT @paymentsLeft = @numPayments - @npmt

	Print 'Updating RecPaymentID: ' + @RecPaymentID + ', pmts processed: ' + cast(@npmt as varchar(5)) + ', next date: ' + cast(@TempDate as varchar(32))
	Update Recurring_Payment set PaymentsProcessed = @npmt, NextProcessDate = @TempDate where current of temp_cursor

	FETCH NEXT FROM temp_cursor INTO @recPaymentID, @initialDueDate, @lastProcessedDate, @numPayments, @frequency
END
CLOSE temp_cursor
DEALLOCATE temp_cursor
return(0)
on_error:
Print 'Error'
return(-1)
GO
