CREATE PROCEDURE [dbo].[wc_RecyclePayments] 
@RunID int
AS
-- First get all the transactions from PAYMENT_HISTORY that failed  and put them back into 
-- the PAYMENT table with the original WillProcessDate so we can run them again
insert into payment (paymentID, payeelistID,Account,Amount,WillProcessDate,RecPaymentID,StatusCode,Memo,BillReference,
LastUpdateDateTime,MemberID,SourceApplication,EntryDate,DueDate )
select paymentID, payeelistID,Account,Amount,WillProcessDate,RecPaymentID,100 as StatusCode,Memo,BillReference,
getdate() as LastUpdateDateTime,MemberID,'Recycle' as SourceApplication,GetDate() as EntryDate,NULL as DueDate 
from Payment_History  WITH (NOLOCK) where RunID = @RunID AND statuscode <> 101 AND 
    RIGHT(RTRIM(Account), 2) = '00'
-- Make changes to the records in PAYMENT_HISTORY so
-- that the members will not see the original transaction
-- and that we will not violate the Primary Key constraint on the PaymentID field
Update Payment_History set 
  PaymentID = '1' + right(PaymentID,len(PaymentID)-1),
  MemberID = 'Recycled' + rtrim(MemberID)
where RunID = @RunID AND statuscode <> 101 AND 
    RIGHT(RTRIM(Account), 2) = '00'
GO
